INSERT INTO `news` (`new_id`, `new_title`, `new_quote`, `new_content`, `new_image`, `new_day`, `new_type_id`, `new_type`, `new_content_detail_2`) VALUES
(1, 'Assassin''s Creed', 'Assassin''s Creed: Brotherhood - Sát thủ thành Rome', 'Phải chờ đợi suốt gần 4 tháng kể từ khi phiên bản dành cho console phát hành, các game thủ nền PC mới được đồng hành cùng Ezio trong việc lập lại trật tự ở thành Rome...', 'ac_1.jpg', '09/05/2013', 2, 'news', '<p>\nAssassin’s Creed với bối cảnh Trung Đông thế kỉ XII đã rất thành công, vì thế Ubisoft đã chuyển hẳn sát thủ của mình sang một vùng đất mới, đầy hấp dẫn và huyền bí, và Ý là sự lựa chọn tuyệt vời.\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ac_1.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\nÝ là đất nước có kiến trúc độc đáo và bí ẩn, có những dòng họ tồn tại cả chục thế kỉ, có những băng đảng Mafia từ rất lâu đời. Và Assassin’s Creed 2 đã chọn một Venice trên nước, hay một Florence tráng lệ, để xây dựng một sát thủ thần thánh.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ac_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 2</p>\n<br />\n<p>\nAssassin’s Creed 2 là một câu chuyện, có mở có kết, có lịch sử, hiện tại và cả tương lai. Một câu chuyện sinh động về Ezio, một người bình thường bị số phận xô đẩy và dần trở thành một sát thủ chuyên nghiệp. Ezio dần dần học các kĩ năng của một sát thủ, lối sống bí ẩn của sát thủ, và đặc biệt là cách “xuống tay” nhanh chóng của một sát thủ giỏi nhất.\n<br />\nCốt truyện của Assassin’s Creed 2 xoay quanh Ezio rất hấp dẫn, đưa người chơi từ hết bất ngờ này đến bất ngờ khác, tạo một cảm giác hưng phấn tột cùng khi nhập vai vào sát thủ này. Các màn chơi diễn ra chậm hơn và lặng lẽ hơn so với một Prince of Persia mang đậm tính hành động. Chính vì thế người chơi sẽ có dịp ngắm cảnh đẹp mê hồn của nước Ý, chiến đấu trong lòng nước Ý, leo lên các tòa nhà của Ý, cảm giác thật tuyệt.\n<br />\nChiếc máy bay đậm chất “da Vinci” mà Ezio dùng để di chuyển trong Assassin’s Creed 2 cũng rất đặc biệt. Nó đơn giản nhưng chính xác, giúp bạn dễ dàng di chuyển trên bầu trời để trốn tránh những tên lính cũng như tiếp cận mục tiêu. Lượn trên không trung và đáp xuống nhẹ nhàng trên những mái nhà sẽ là việc bạn phải làm quen sớm trong Assassin’s Creed 2.\n</p>\n<img src="http://localhost/CIF2/public/images/image-new/ac_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 3</p>\n<br />\n<p>\nAssassin’s Creed 2 diễn ra như một bộ phim hành động phiêu lưu chân thực với sự chậm rãi của cốt truyện. Có thể có người thích, có người không thích, nhưng phải khẳng định Assassin’s Creed 2 là một tác phẩm nghệ thuật thực thụ, dành cho những game thủ muốn từ từ nhìn ngắm và tận hưởng game.\n<br />\nƯu điểm Hình ảnh tuyệt đẹp, dựa trên những con phố của nước Ý Âm thanh chân thực, lồng tiếng rõ ràng và chi tiết Cốt truyện nhiều lớp, hấp dẫn Gameplay tốt với những màn phiêu lưu thú vị Nhược điểm Diễn biến game hơi chậm Chiến đấu không quá khó khăn cho các game thủ kinh nghiệm\n</p>'),
(2, 'Devil May Cry 5', 'Devil May Cry 5 : Thế giới chết nổi dậy', 'DmC Devil May Cry ngay từ những trailer đầu tiên đã bị các fan gạo cội của dòng game này dập tơi bời. Lý do chủ yếu cho đợt “biểu tình” này đến từ ngoại hình của nhân vật Dante mới.', 'dmc_1.jpg', '29/04/2013', 3, 'news', '<p>\nDòng game Devil May Cry kể từ khi được ra mắt lần đầu tiên bởi nhà thiết kế game tài ba này tới nay cũng đã bước sang phiên bản "thứ 5" (không phải số 5) là DmC: Devil May Cry dưới bàn tay của một studio phương Tây - Ninja Theory. Trong khi series vốn đã nổi tiếng với hình ảnh nhân vật chính Dante lãng tử và đôi phần ngạo mạn trước đây thì việc thay đổi sang một ngoại hình được cho là ít hấp dẫn hơn lập tức nhận được sự phản đối mạnh mẽ từ cộng đồng người hâm mộ ngay từ khi được công bố tại Tokyo Game Show năm 2010.\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/dmc_1.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\nĐội ngũ phát triển của Ninja Theory cũng rất vui mừng khi đáp lại: “Thành thật cảm ơn vì những ngôn từ tốt đẹp ông dành cho chúng tôi. Bản thân chúng tôi cũng là những người hâm mộ vô cùng lớn với công việc mà ông đang làm và không thể chờ để được thưởng thức Metal Gear Rising (tựa game đang được Kamiya phát triển) sắp tới.”\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/dmc_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 2</p>\n<br />\n<p>\nĐây thật sự là những cử chỉ rất tốt đẹp trong thế giới game ngày nay, khi giữa một bên là người có công gây dựng và một bên là người kế tục những giá trị cũ. Với những lời khen ngợi từ chính cha đẻ của dòng game này, cũng như những đánh giá cao từ giới chuyên môn, chắc hẳn tựa game DmC: Devil May Cry sẽ là một món ăn không thể thiếu cho cộng đồng game thủ tháng đầu năm 2013.\n</p>\n<img src="http://localhost/CIF2/public/images/image-new/dmc_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 3</p>\n<br />'),
(3, 'Điểm đánh giá trung bình của PES 2013 là 8/10', 'Điểm đánh giá trung bình của PES 2013 là 8/10', 'Trang tin CVG nhận định "PES 2013 là phiên bản tốt nhất trong tất cả thế hệ, mang đến sự thú vị và cảm hứng của một trò chơi hiện đại". Điểm số đánh giá dành cho game là 91/100.', 'pes_1.jpg', '10/05/2013', 5, 'news', '<p>\nCòn OXM (Official Xbox Magazine) cho PES 2013 số điểm là 8/10 với lời khen tặng "Không thể phủ nhận rằng PES 2013 đã có một bước cải tiến lớn so với các trò chơi trước đó".\n<br />\nĐó là với Xbox 360, còn đánh giá của OPM (Official PlayStation Magazine) trên PS3 thì xác nhận "những lỗi quen thuộc vẫn còn tồn tại, tuy nhiên trò chơi đã có bước chuyển mình ấn tượng trên hệ máy PS3". Điểm đánh giá là 8/10.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/pes_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nVới IGN, số điểm dành cho game bóng đá của hãng Konami là 8,5/10. "Không hoàn hảo những PES 2013 có nhiều điểm cộng hơn các phiên bản tiền nhiệm. Trò chơi khiến game thủ cảm nhận được sự thay đổi về bản sắc, định hướng và phong cách của hãng phát triển", IGN nhận xét.\n<br />\nVideogamer thì nói rằng: "Sản phẩm này của Konami mang lại giá trị tương ứng giống như game Tekken, luôn đem lại cảm giác hứng khởi và thú vị mỗi khi người chơi bấm nút Start". Trang game này cũng cho PES 2013 số điểm 8/10.\n<br />\nKhông khen ngợi nhiều như các "đồng nghiệp", Metro, một trang tin cũng khá tiếng tăm tại Anh chỉ đánh giá mức điểm 7/10 cho phiên bản PES 2013. "Có cảm giác chúng ta vẫn đang chơi một game cổ điển. Vẫn còn những khía cạnh khá ''thô'' mà trò chơi chưa thể rũ bỏ", là nhận định của trang tin này.\n<br />\nVới PSU (PlayStation Universe), "những cải tiến về đồ họa là thực sự quan trọng, chất lượng". Số điểm mà trò chơi nhận được trong bài nhận xét là 8,5.\n<br />\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/pes_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 2</p>\n<br />\nDường như muốn xoa dịu mối hận thù lâu năm giữa hai dòng game bóng đá nổi tiếng, trang tin Nowgamer chia sẻ: "FIFA và PES sẽ tiếp tục tranh đấu trong một thời gian dài nữa. Vẫn như mọi năm, không có gì là hoàn hảo. Tuy nhiên, người chơi sẽ không phải thất vọng khi lựa chọn sản phẩm này". Điểm số hào phóng mà Nowgamer dành cho PES 2013 là 9,1/10.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/pes_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nNhìn chung, đánh giá của các trang game trên thế giới về PES 2013 là khá đồng đều và khiến cả hãng phát triển lẫn những fan hâm mộ trung thành thỏa mãn. Tuy nhiên, dù sao chăng nữa thì chính mỗi game thủ sẽ có những số điểm riêng của mình dành cho trò chơi sau khi tận tay trải nghiệm sản phẩm vào ngày 20/9 tới đây.\n</p>'),
(4, 'Ninja Gaiden 3: Razor', 'Một mình giữa vòng vây trong Ninja Gaiden 3: Razor''s Edge', 'Ngoài việc tái hiện các nội dung ban đầu, đồ họa game đã được cải tiến cùng gameplay chi tiết hơn với sự hỗ trợ của máy console mới do hãng Nintendo phát triển.', 'ng_1.jpg', '11/05/2013', 1, 'news', '<br />\n<p>\nPhiên bản này sẽ có tính năng AI thông minh hơn, hệ thống nâng cấp và nhiều tính năng đặc biệt khác. Người chơi sẽ được thưởng tiền bằng cách thực hiện các đòn tấn công liên hoàn đẹp mắt, sau đó dùng nó để trang bị vũ khí, nhẫn thuật... Các hướng dẫn người chơi thực hiện combo được trình chiếu qua Gamepad.\n<br />\nGame sẽ lên kệ chính thức vào giữa tháng 11 tới đây.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ng_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ng_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ng_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ng_5.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />'),
(5, 'The Witcher 2', 'The Witcher 2 - Sói trắng miền băng giá', 'Chàng Geralt xứ Rivia đã trở lại trong một cuộc phiêu lưu mới, vì danh dự và tình yêu..Số phận sẽ ra sao trước 1 thế giới bao trùm là lũ ác quỷ ?', 'tw_1.jpg', '08/05/2013', 2, 'news', '<p>\nGeralt xứ Rivia, thợ săn quái vật đầy quyền năng xuất hiện với một ký ức hoàn toàn trống rỗng và một bản năng chiến đấu vẫn sắc bén như ngày nào. Những tưởng anh chàng sẽ yên phận trong vai trò một cận vệ hoàng gia, nhưng một cuộc ám sát thành công giết chết hoàng đế Foltest đã khiến Geralt bị tống vào ngục, và chỉ có sự tin tưởng của đồng đội mới giúp anh thoát khỏi nhà tù để khởi đầu cho hành trình rửa sạch danh dự của chính mình. Trong cuộc phiêu lưu này, sói trắng Geralt không hề đơn độc, bởi bên anh luôn có những người bạn đồng hành và hai thanh kiếm đáng tin cậy nhất của mình.\n</p>\n\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/tw_1.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nLối chơi của The Witcher 2 có một vài cải tiến mới so với phần đầu, nhất là hệ thống chiến đấu của game. Nếu đã chơi qua phần một, bạn sẽ biết rằng hệ thống chiến đấu của game có khác biệt rất lớn so với những game nhập vai khác: việc click chuột "điên cuồng" sẽ chỉ khiến người chơi ăn đòn nhừ tử, thay vì "cày" xuyên qua hàng ngũ quái vật cản đường. Mỗi cú click chuột trong game đòi hỏi bạn phải chọn đúng thời cơ để tạo nên một chuỗi combo đẹp mắt và nguy hiểm để đánh hạ đối thủ. Được tiếp sức bằng các loại sign (phép thuật) và các loại thuốc được tạo ra từ thuật giả kim (alchemy), Geralt hoàn toàn có thể hạ gục mọi loại đối thủ, từ những con ác thú to lớn nhất đến những kẻ thù nhanh nhẹn nhất. Để gia tăng hiệu quả của ba phong cách chiến đấu này, nhân vật chính của chúng ta có 51 kỹ năng dành cho bốn đường: huấn luyện, kiếm sĩ, giả kim và phép thuật, mỗi kĩ năng lại có 2 cấp độ, có thể học bằng điểm có được mỗi khi lên cấp.\n</p>\n\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/tw_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nBên cạnh việc chiến đấu phụ thuộc vào kiếm, phép thuật và các loại thuốc giả kim, Geralt còn được phụ trợ bằng một số công cụ khác: bom và bẫy. Không như các loại thuốc chỉ có thể tự tay tạo ra, bom và bẫy có thể mua được từ các cửa hàng, hoặc chế tạo bằng các sơ đồ tìm được khắp nơi trong game. Mặc dù chỉ là vũ khí phụ, nhưng chúng hết sức hữu hiệu trong những tình huống đối đầu với số đông, khi có khả năng gây choáng hoặc gây thiệt hại lâu dài lên nhiều mục tiêu cùng lúc, hoặc có tác dụng phụ trợ cho những kỹ năng của Geralt. Cùng với các vật phẩm như trang phục, vũ khí, nguyên liệu chế thuốc thì bom và bẫy cũng góp phần làm phong phú hệ thống trang bị của game. Nhiều món đồ và kỹ năng còn có "lỗ" (socket) cho phép người chơi nâng cấp thêm cho chúng.\n</p>\n\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/tw_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nGame sử dụng engine hoàn toàn mới mang tên Red Engine, hỗ trợ những công nghệ mới nhất của DirectX 11, trong đó ấn tượng nhất là Ubersampling, công nghệ dựng một khung hình nhiều lần nhằm mang lại chất lượng hình ảnh cao nhất và loại bỏ răng cưa trên vật thể. Tất nhiên, công nghệ này chỉ dành cho những cỗ máy của tương lai, bởi ngay cả với một card đồ họa GTX580 và CPU i7, Ubersampling có thể kéo khung hình của The Witcher 2 xuống dưới mức 20. Nếu bỏ qua Ubersampling, game vẫn mang lại chất lượng khung hình khá tốt ngay cả ở mức trung bình kể cả khi ở trong một ngôi làng sầm uất hay giữa khu rừng rậm tươi tốt. Các cử động của nhân vật được làm rất tốt, tuy nhiên biểu cảm trên gương mặt còn khá cứng nhắc.\n</p>\n\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/tw_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n'),
(6, 'Need 4 Speed: The Run', 'Những cảm nhận đầu tiên về Need for Speed: The Run', 'The Run là mẫu đầu tiên cho một phân nhánh mới của dòng game Need For Speed. Trong series này, người chơi sẽ vào vai một nhóm dân ưa mạo hiểm bị rơi vào một tình huống sống còn và phải chạy trốn vì sinh mạng của mình.', 'nfs_1.jpg', '07/05/2013', 6, 'news', '<p>\nCốt truyện xoay quanh chàng trai Jack bị thế lực xấu xa đe dọa tính mạng, lối thoát duy nhất của anh ta phải chiến thắng cuộc đua xuyên nước Mỹ, đồng thời phải né tránh những cuộc săn đuổi của kẻ thù.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/nfs_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nNgười chơi sẽ trải qua những vòng đua ở các thành phố lớn trên khắp nước Mỹ, khởi hành từ San Francisco và đích đến là New York. Theo nhà sản xuất, các thành phố mà game thủ sẽ có dịp được chiêm ngưỡng gồm San Francisco, Chicago, Las Vegas, Denver, Detroit và New York.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/nfs_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nCũng như những game Need for Speed trước, The Run sẽ mang những "xế hộp" ngoài đời thực vào game, từ những siêu xe cho đến ôtô tầm trung của các hãng sản xuất nổi tiếng trên thế giới. Hiện tại đã có 5 xe được điểm mặt:\n<br />\n- Audi R8 Coupe 5.2<br />\n- Ford Shelby GT500<br />\n- Porsche 911 GT2<br />\n- BMW E92 M3 GTS<br />\n- McLaren MP4-12C<br />\n\nLần đầu tiên trong series Need for Speed, game thủ có thể đặt chân ra khỏi chiếc xế hộp ngột ngạt để khám phá những ngóc ngách của thành phố. Tuy nhiên, điều này chỉ mang tính tạm thời, bởi những pha hành động của nhân vật ở môi trường bên ngoài chỉ diễn ra theo những sự kiện định sẵn của game, hơn nữa người chơi cũng không thể trực tiếp điều khiển nhân vật theo ý muốn.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/nfs_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nGame sẽ hỗ trợ chơi mạng qua LAN và chơi trực tuyến, hoàn thành tốt những cuộc đua trong mục chơi mạng bạn sẽ unlock được những chiếc xế hộp mới cùng nhiều thứ thú vị khác. Hệ thống Autolog của Need for Speed Hot Pursuit hồi năm ngoái cũng sẽ có mặt trong The Run, có chức năng ghi lại và so sánh thành tích của những người chơi trong mục chơi mạng, điều này thúc đẩy tính cạnh tranh cao hơn, làm những cuộc vui trở nên hào hứng, quyết liệt hơn.\n</p>\n'),
(7, 'Call Of Duty 4 ', 'Call Of Duty 4 : Modern Warfare - Nhìn lại chặn đường dài phát triển', 'Cùng điểm lại quá trình phát triển của dòng game hành động này.', 'cod_1.jpg', '11/05/2013', 1, 'news', '<p>\nKể từ phiên bản đầu tiên ra đời ngày 29/10/2003, Call Of Duty đã khẳng định được tên tuổi của mình trong thể loại game hành động. Và cũng từ đó, Call Of Duty cũng đã không ngừng đổi mới, nâng cấp và chuyên nghiệp trong quá trình thiết kế để thỏa mãn nhu cầu ngày càng lớn của người chơi.\n<br />\nĐến năm 2011, thống kê cho thấy Call Of Duty đã bán được hơn 100 triệu đĩa của nhiều phần khác nhau. Tới năm 2012, Call Of Duty đã có hơn 4 triệu lược người chơi hàng tháng và hơn 1,6 tỉ giờ chơi online trên toàn thế giới\n</p>\n<br />\n<b>Call Of Duty</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nLấy cảm hứng từ Thế chiến thứ II, Call Of Duty đã đưa người chơi hòa vào bầu không khí khói lửa của những năm Thế chiến. Làm cho người chơi phải di chuyển, bắn, né và đánh giáp lá cà để sống sót và hoàn thành nhiệm vụ.\n<br />\nĐược Infinity Ward phát triển và Activision phát hành, game mô phỏng bộ binh và những vũ khí tác chiến của Thế chiến thứ II. Người chơi sẽ vào vai bộ binh Mĩ, lính nhảy dù Anh và Hồng quân Liên Xô.\n</p>\n<br />\n<b>Call Of Duty 2</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nCũng được phát triển bởi Infinity Ward và được phát hành bởi Activision vào ngày 25/10/2005. Trong phiên bản này, cốt truyện cũng không khác gì nhiều so với phiên bản trước. Người chơi cũng sẽ nhập vai vào lính thủy đánh bộ Mĩ, quân đội Anh và Hồng quân Liên Xô. Tuy có nhiều nét tương đồng với người anh đi trước, nhưng Call Of Duty 2 lại nổi bật hơn với đồ họa đẹp và những chiến trường với quy mô rộng lớn hơn. Một lần nữa, tên tuổi của Call Of Duty lại được khẳng định.\n</p>\n<br />\n<b>Call Of Duty 3</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nKhác với hai hai phiên bản trước, Call Of Duty 3 được phát triển bởi Treyarch và được Activision phát hành ngày 7/11/2006. Vẫn tiếp tục đề tài xoay quanh Thế chiến thứ II, người chơi sẽ tham gia vào hàng loạt chiến dịch như: chiến dịch Mĩ, chiến dịch Anh- Pháp, chiến dịch Canada, chiến dịch Ba Lan. Ở phiên bản này, ngoài lối chơi truyền thống FPS (góc nhìn thứ nhât), game cũng được hổ trợ thêm TPS (góc nhìn thứ ba). Nhưng game không được phát hành trên hệ thống PC vì một vài lý do trong nội bộ.\n</p>\n<br />\n<b>Call Of Duty 4: Modern Warfare</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_5.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nSau vài rắc rối trong phiên bản thứ ba, vào ngày 7/11/2007, phiên bản thứ tư đã ra đời với sự trở lại của nhà phát triển Infinity Ward. Đây được xem là một bước ngoặt mới của dòng game Call Of Duty. Phiên bản Call Of Duty 4: Modern Warfare thoát ra khỏi sự ràng buộc của Thế chiến thứ II để bước vào một cuộc chiến của tương lai. Với cốt truyện rộng mở, hành động nghẹt thở, vũ khí đa dạng và được thiết kế công phu, bản Call Of Duty 4: Modern Warfare đã tạo nên một cơn sốt trên thị trường game quốc tế với việc bán được 13 triệu đĩa. Ngoài ra, bản Call Of Duty 4: Modern Warfare cũng là game đầu tiên được đánh giá là Mature từ ESRB.\n</p>\n<br />\n<b>Call Of Duty 5: World At War</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_6.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nPhát hành ngày 8/11/2008, game - do Treyarch phát triển - tuy không tiếp nối câu truyện của Call Of Duty 4: Modern Warfare, Call Of Duty 5: World At War nhưng lại đưa người chơi quay trở về những năm thế chiến thứ II. Chính việc này cộng thêm việc giao quyền phát triển cho Treyarch, Call Of Duty 5: World At War đã làm phần lớn fan cảm thấy không tin tưởng. Nhưng không phải vậy, Call Of Duty 5: World At War đã không làm các fan thất vọng, chỉ những phút chơi đầu tiên, mọi hoài nghi đã bị xóa bỏ nhờ những pha hành động nghẹt thơ, đồ họa tuyệt vời và thiết kế nhân vật tinh xảo. Ở phiên bản này, người chơi sẽ vào vai lính thủy đánh bộ Mĩ hoạt động tại mặt trận Thái Bình Dương, quân đội Anh và Hồng quân Liên Xô trong những ngày hấp hối của phe phát-xít.\n<br />\nBắt đầu từ đây, chúng ta có thể nhìn thấy rõ việc "Activision đã quyết định hợp tác cùng lúc với Infinity Ward và Treyarch". Nghe có vẻ bất ngờ, nhưng thật chất cũng rất đỗi bình thường. Qua 5 phần trên ta có thể thấy, cứ 1 năm thì Activition sẽ cho ra đời một phần mới của Call Of Duty. Thế nhưng, để đáp ứng nhu cầu ngày càng cao của người chơi thì quả thật 1 năm là không đủ. Vì thế, thay vì để Infinity Ward gánh tảng đá lớn ấy thì Activision đã quyết định cho Treyarch vào cuộc để cân bằng thế cuộc. Tuy thất bại ở Call Of Duty 3, thế nhưng cũng không phải là thất bại hoàn toàn nên Activition đã chấp nhận cho Treyarch thử lại một lần nữa ở Call Of Duty 5 và đó là bước đi sáng suốt của Activition. Như vậy, chất lượng của Call Of Duty luôn được đảm bảo và ngày một nâng cao.\n</p>\n<br />\n<b>Call Of Duty 6: Modern Warfare 2</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_7.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nPhát triển bởi Infinity Ward và phát hành vào ngày 10/11/2009. Call Of Duty 6: Modern Warfare 2 tiếp bước câu truyện của phần bốn, với những diễn biến của năm năm tiếp theo. Ở phiên bản này, danh tiếng của Modern Warfare lại một lần nữa được vang xa với việc bán được hơn 22 triệu đĩa trên toàn thế giới và rất nhiều lời phê bình tích cực từ nhiều website nổi tiếng.\n</p>\n<br />\n<b>Call Of Duty 7: Black Ops</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_8.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nGame được phát tiển bởi Treyarch và phát hành vào ngày 9/11/2010. Đây là bước đi mới của Treyarch trong quá trình hợp tác với Activision. Treyarch đã không còn nói về mảng đề tài Thế chiến thứ II cũ rích, mà thay vào đó là một cuộc chiến viễn tưởng nhưng cũng dựa trên một số sự kiện có thật. Người chơi sẽ vào vai nhận vật có tên là Mason và tham gia và hành trình truy tiềm và ngăn cản việc phát triển vũ khí sinh học có tên là Nova 6. Game có cấu hình được nhiều người chơi đánh giá là tuyệt vời. Nhưng game vẫn chưa có hiệu ứng vật lý.\n<br />\nTuy nhiên, ở phần 7 này, Call Of Duty không được đại đa số người chơi Việt chào đón do có nội dung lệc lạc về chiến tranh ở Việt Nam.\n<br />\nCall Of Duty 7: Black Ops có hơn 5,4 triệu đĩa được bán chỉ trong vòng 24 tiếng sau khi phát hành, phá vở kỉ lục của Call Of Duty 6: Modern Warfare 2. Và vào 3 /6/2011, Activision công bố đã bán được hơn 25 triệu đĩa trên toàn thế giới.\n</p>\n<br />\n<b>Call Of Duty 8: Modern Warfare 3</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_9.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nLần này, game được Infinity Ward phát triển. Ngày phát hành là 8/11/2011. Để phát triển thành công Call Of Duty 8: Modern Warfare 3 còn có sự giúp sức của Sledgehammer Game và Raven Software. Tiếp nói câu chuyện của Modern Warfare 2, cuộc chiến trở nên ác liệt hơn khi vũ khí sinh học đã được rải xuống các nước như Đức, Pháp, Anh. Với đồ họa tuyệt đẹp, lại cộng thêm những pha hành động gay cấn, hiệu ứng vật lý cũng được thêm vào và khá chân thật. Trong vòng 24 tiếng đầu tiên, Activision đã bán được 6,5 triệu đĩa, doanh thu ước tính 400 triệu USD\n</p>\n<br />\n<b>Call Of Duty 9: Black Ops 2</b>\n<img src="http://localhost/CIF2/public/images/image-new/cod_10.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nDự kiến trình làng ngày 13/11/2012, Call Of Duty 9: Black Ops 2 sẽ tiếp nói câu truyện của Black Ops, nhưng lần này sẽ nói về cuộc chiến tranh hiện đại của năm 2025. Khi mà căng thẳng giữa các nước đã leo thang tới đỉnh điển và chiến tranh bắt đầu. Trong cuộc chiến này , không còn là cuộc chiến dựa hoàn toàn vào sức người nữa, mà nó là cuộc chiến của công nghệ với việc tham gia của robot, các thiết bị chiến đấu không người lái và chiến tranh không gian mạng. Game được tích hợp nhiều kinh nghiệm và thành công của các phiên bản trước nên sẽ là một chấn động mới cho thi trường game quốc tế.\n<br />\nNgoài các bản chính này ra, Call Of Duty còn có các bản phụ khác. Nhưng nổi bậtt nhất là 2 bản phụ Zombie của Phiên bản Call Of Duty 5: World At War và Call Of Duty 7: Black Ops dựa trên những map có sẳn trong phần chơi chính và một số map mới do Treyarch thêm vào.\n<br />\nGiới game thủ hiện đang hy vọng vào cuối năm nay hoặc đầu năm sau, sẽ có trải nghiệm không thể nào quên với phiên bản Call Of Duty mới nhất.\n</p>\n'),
(8, 'Dead Space 3 : Awakend', 'Dead Space 3 : Awakend - Nổi ám ảnh kinh hoàng', 'Dù đã hơn một tháng trôi qua kể từ ngày Dead Space 3 được phát hành chính thức nhưng cho tới thời điểm này, sự thắc mắc và tò mò của các fan hâm mộ về tính mệnh người anh hùng Isaac Clarke cùng người bạn đồng hành John Carver vẫn còn đang rất nóng hổi trên khắp các diễn đàn.', 'ds_1.jpg', '12/05/2013', 3, 'news', '<p>\nSau những nỗ lực không biết mệt mỏi nhằm kích hoạt trở lại cỗ máy phá hủy Brother Moon, hai người anh hùng của vũ trụ đã bị cuốn bay đi trong cơn bão khổng lồ mà vụ va chạm của mặt trăng Necromorph với hành tinh Tau Volantis tạo ra. Mặc cho cơ hội sống sót của họ trong thảm họa đó gần như là con số không, vẫn có không ít những niềm hy vọng về điều thần kì xảy ra được ẩn giấu đâu đó trong những thước phim cuối cùng của phần 3. Và những hình ảnh đầu tiên của Awakened đến từ một đống đổ nát nơi mà Isaac mở mắt tỉnh dậy sau một giấc ngủ dài cứ ngỡ như là mãi mãi. Sự sống sót của anh cùng người bạn đồng hành Carver khó tin đến mức cả hai đã nhiều lần phải tự kiểm chứng xem mình còn sống hay đã chết.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ds_1.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nTrở lại với những vấn đề của thực tại, cả hai nhận ra rằng họ có thể sẽ bị kẹt lại ở Tau Atlantis mãi mãi nếu không may mắn tìm được một con tàu không gian nào đó còn nguyên vẹn sau thảm họa vừa xảy ra. Isaac và Carver sẽ phải bắt đầu một cuộc hành trình mới đi tìm đường trở về Trái Đất bằng việc quay lại những nơi họ đã từng bước chân qua, vào lại những chiếc thang máy đã từng cứu họ khỏi bọn quái vật Necromorph với hy vọng thấy được chút gì đó hy vọng. Rồi cuối cùng, những chiếc thang máy cũng đưa họ tới Terra Nova, chiếc phi thuyền khổng lồ Sovereign Colonies từng hạ cánh ở đây 200 năm trước.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ds_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nNgoài những con Necro Scissorhand, Awakened còn mang đến cho cuộc hành trình những kẻ ngáng đường khác mà đáng chú ý nhất là phiên bản nâng cấp của Stalker. So với con quái vật gốc, sinh vật trong Awakened được tăng cường thêm sức mạnh giúp khỏe hơn, nhanh hơn và hung hãn hơn. Nếu đặt lên bàn cân so sánh giữa đội quân của Danik và kẻ cuồng tín mới Carr thì chúng ta có thể thấy sức mạnh cùng khả năng tấn công chớp nhoáng vượt trội của kẻ mới đến so với tên tội phạm trước đó.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/ds_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nTuy vậy, nhìn chung, phiên bản mở rộng này ít thấy có ấn tượng của những sáng tạo mới mà chủ yếu giúp cho người chơi có thêm thời gian giải trí dựa trên nền cốt lõi của Dead Space 3 cùng một câu truyện hoàn toàn khác. Chưa thực sự hoàn hảo trong cách phát triển diễn biến câu chuyện tìm đường trở về Trái Đất của Isaac và Carver nhưng Visceral Games đã thực sự thành công trong việc tạo ra một cuộc phiêu lưu đáng sợ và gây ám ảnh hơn rất nhiều so với những trải nghiệm có phần “nhẹ đô” của Dead Space 3.\n</p>'),
(9, 'Crysis 3 - Return To The World', 'Crysis 3 - Khi người hùng thầm lặng trở về', 'Cell không phải là mối nguy thực sự của toàn thế giới. Chủng loài Cepth hùng mạnh vẫn còn đó.', 'cs_1.jpg', '12/05/2013', 1, 'news', '<p>\nHiếm có game FPS nào gây được ấn tượng giống như series Crysis đã từng làm. Một kiệt tác của con người trong làng game, tất cả gần như đạt đến mức độ hoàn hảo: cốt truyện, đồ họa, âm thanh và thậm chí là cả những chi tiết nhỏ nhặt nhất. Không ngoa khi nói rằng mỗi lần ra mắt phiên bản mới lại là một lần tỏa sáng của dòng game cũng như của nhà sản xuất Crytek. Trong mắt những game thủ khó tính nhất, Crysis là một sản phẩm có thể làm cho họ hoàn toàn thỏa mãn và chưa bao giờ xuống phong độ cả.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/cs_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nNối tiếp các sự kiện diễn ra từ những bản trước, hơn 20 năm trôi qua kể từ cuộc xâm lược lần thứ hai vào New York, dù đã tự hi sinh bản thân mình bởi phôi nhiễm virus Manhattan nhưng kí ức của Prophet đã được lưu lại trong Nanosuit và cuối cùng hòa làm một với thân xác của Alcatraz. Thời gian này tập đoàn Cell đã tranh thủ sự hỗ loạn do loài Cepth gây ra để bành trướng cả về quy mô và tầm ảnh hưởng để tiến tới thống trị toàn thế giới. Prophet cùng với sự giúp đỡ của người đồng đội cũ Psycho liệu có chặn đứng được âm mưu này? Liệu loài Cepth đã thực sự bị đánh bại và thế giới không còn có nguy cơ bị xâm chiếm? Tất cả chỉ được trả lời sau khi game thủ kết thúc các màn chơi trong Crysis 3.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/cs_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nGame chỉ có khoảng 2 trận đấu boss và kết thúc khiến cho người chơi cảm thấy vô cùng hụt hẫng. Sự kết hợp giữa tuyến tính và kết thúc mở có thể làm nhiều người cảm thấy thích thú, bởi Crytek vẫn duy trì việc cho phép người chơi tự do lựa chọn việc lên kế hoạch, tiếp cận và tấn công mục tiêu với tốc độ cực nhanh. Tuy nhiên với những ai đề cao về cốt truyện thì Crysis 3 không thể làm họ cảm thấy hài lòng. Nội dung quá dễ đoán và không để lại được ấn tượng như series game Call of Duty: Modern Warfare, tuy nhiên không thể chê trách được vì cốt truyện vốn không phải là thế mạnh của dòng game Crysis.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/cs_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/cs_5.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n\n<p>\nSở hữu những tiềm năng có thể biến Crysis 3 thành game FPS của năm, thế nhưng dường như Crytek lại quá tập trung vào việc đưa phần 3 của cuộc phiêu lưu đến tương lai loài người trở thành một màn phô diễn đồ họa. Dĩ nhiên, họ đã thành công, thế nhưng phản ứng phụ kéo tới, biến Crysis 3 trở thành một game với chất lượng chỉ dừng lại ở mức khá.\n<br />\nRõ ràng, để thành công, Crysis 3 cần rất nhiều thứ như thời lượng chơi dài hơn, cải tiến AI, đem lại nhiều thử thách hơn cho game thủ. Có lẽ đến lúc để Crytek xem xét nghiêm túc lại về việc phát triển game theo chiều hướng nào cho đúng đắn, bởi tình huống xấu nhất là sẽ có ngày Crysis mất đi vị trí của mình trong lòng người chơi. Đây là điều tuy thiếu nhưng không phải là không có trong làng game vậy.\n<br />\n<b>Ưu điểm :</b>\n<br />\n- Đồ họa tuyệt vời, có sự kết hợp hài hòa giữa môi trường tự nhiên và đô thị\n<br />\n- Vũ khí độc đáo, đặc biệt là cây cung\n<br />\n- Khả năng chiến đấu linh hoạt nhờ bộ Nanosuit<br />\n\n- Chế độ Multiplayer tốt<br />\n\n<b>Nhược điểm :</b><br />\n\n- Thời gian ngắn chỉ khoảng 5-6 tiếng đồng hồ<br />\n\n- Quá dễ chơi<br />\n\n- AI không được tốt<br />\n<br />\n</p>\n'),
(10, 'Battle Field 3 : Close Quarters - '' Hương vị '' Call Of Duty', 'Battle Field 3 : Chuyến Tuyến Rực Lữa', 'Một lợi thế duy nhất và không thể chối cãi của Battlefield 3 so với các tựa Game khác, đặc biệt với kẻ thù không đội trời chung Call of Duty: Modern Warfare 3, là một nền đồ họa vô cùng tân tiến, khả năng điều khiển các khí tài quân sự cùng phong cách đấu súng tả thật pha trộn một chút tính chiến thuật bên trong', 'bf_1.jpg', '13/05/2013', 1, 'news', '<p>\nVà để thực hiện một lối đi an toàn cho mình, không còn cách nào hơn là theo dấu chân thành công của người khác. Và nếu Call of Duty có Elite, Battlefield 3 sau đó có Premium. Call of Duty có những bản đồ nhỏ hẹp, mà trong đó là những trường đoạn đấu súng tốc độ nhanh, những pha HeadShot chớp nhoáng thì bây giờ, ở DLC Close Quarters, Battlefield cũng có những khu vực nội cảnh với 10 loại vũ khí tầm gần và một chế độ chơi mới.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/bf_1.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nHoặc như bản đồ Donya Fortress, một biệt thự nhiều tầng lầu, mang phong cách Trung Đông với 1 điểm Command Point nằm ngay giữa đại sảnh , trong khi 2 điểm còn lại, 1 nằm trên tầng, 1 nằm dưới đường hầm bên dưới. Trong khi Scrapmetal, một bản đồ có 2 nhà kho mục nát được nối với nhau qua một cây cầu với lối đi cực kỳ lộ liễu trước tầm ngắm của kẻ địch. Và cả 2 bản đồ này đều có những con đường riêng để cho những tay chuột ưa rình mò, ẩn nấp có thể tới được Command Point trong im lặng. Duy chỉ có bản đồ Ziba Tower là có nhiều thiếu sót trong khâu thiết kế, khi với một môi trường dạng chữ U, tạo ra nhiều điểm “thắt cổ chai”, gây khó khăn khi di chuyển số đông và tạo điều kiện dễ dàng cho đối phương hạ thủ.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/bf_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nNhưng cứ mỗi bước đi thành công của Close Quarters, là thêm một bước Battlefield 3 đánh mất đi chính cái tên riêng của mình. Đơn giản chỉ vì một điều, đó là không thể cấy những pha đụng xe tan tành vào Gran Turismo và giả vờ nó là Burnout được, cũng như việc không thể đem cái chất đấu súng như vũ bão, dồn dập của Call of Duty rồi nhét nó vào Gameplay hơi hướng chiến thuật và phong cách hành động tả thật của Battlefield 3. Mặc dù nó đang đem tới cái lợi trước mắt cho bản thân hãng, nhưng liệu trong tương lai, DICE có tự đi tìm một con đường thành công riêng cho bản thân mình, hay là cứ tiếp tục làm một bản sao khá, nhưng chưa đủ tầm của Call of Duty? Vậy hãy cùng chờ đợi DLC tiếp theo của Battlefield 3 – Armored Kill, phiên bản sẽ đem người chơi tới môi trường rộng lớn nhất từ trước tới nay với những khí tài quân sự, một điểm đã làm nên cái tên Battlefield.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/bf_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />'),
(11, 'Far Cry 3 : Survival In Dead Island', 'Far Cry 3 : Sinh tồn trên đảo chết', 'Trở lại với hòn đảo nhiệt đới, Far Cry 3 đã có nhiều thay đổi để không bị xem là bản sao của phiên bản đầu tiên.', 'fc_1.jpg', '14/05/2013', 1, 'news', '<br />\n<img src="http://localhost/CIF2/public/images/image-new/fc_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nFar Cry 3 là một tựa FPS thế giới mở khá giống với Far Cry 2, nhưng với tất cả những sai lầm của Far Cry 2 đã được loại bỏ. Bối cảnh Phi châu được thay bằng Rook, một hòn đảo nhiệt đới trù phú ở Nam Thái Bình Dương, và sắc khô vàng nắng cháy được thay bằng một màu xanh mướt bạt ngàn của đồng cỏ, rừng cây và biển cả. Tuy nhiên, ẩn dưới màu xanh thanh bình lại là một thế giới hoàn toàn khác, nơi luật rừng được áp đặt lên cả dã thú và con người sinh sống trên hòn đảo này.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/fc_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nCách mà Far Cry 3 khởi đầu cuộc chơi của mình tỏ ra hết sức ấn tượng, khi chỉ một giây trước bạn còn là một chàng trai sung sướng và hạnh phúc cùng bạn bè trong chuyến du lịch tuyệt vời, thì chỉ một giây sau bạn đang nằm trong tay bọn hải tặc man rợ. Sự xuất hiện của Vaas, một trong hai nhân vật phản diện chính của trò chơi tỏ ra hết sức ấn tượng và lột tả được tính cách điên rồ mà Ubisoft đã xây dựng. Ngay cả những con người trên hòn đảo này cũng không một ai là bình thường, mà tất cả đều có những mặt tăm tối ẩn sâu trong đáy lòng – một điều dễ hiểu nơi mà sự điên rồ đang ngự trị.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/fc_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nCảm thấy chán nản với súng đạn và giết chóc? Far Cry 3 vẫn có những hoạt động dành cho bạn. Game thủ sẽ không bao giờ thiếu việc để làm trong Far Cry 3, mà ngược lại những hoạt động bên lề mà game cung cấp tỏ ra cực kỳ phong phú và có thể khiến bạn mải mê nhiều giờ liền cho đến khi giật mình nhận ra rằng mình vẫn còn vài nhiệm vụ chính chưa hoàn thành. Có thể kể đến những cuộc săn lùng cổ vật, tìm kiếm các lá thư thất lạc của lính Nhật, sưu tập các thẻ nhớ (memory card) của bọn hải tặc, săn thú để chế tạo trang bị, unlock các tháp radio và một vài pha bay lượn ngoạn mục trên diều lượn (hang glider) hoặc wingsuit. Ngoài ra, việc tiêu diệt hải tặc của Vaas là một thử thách rất, rất, rất thú vị bởi phần thưởng kinh nghiệm và sự thỏa mãn lớn lao mà game mang lại khi người chơi thành công tiêu diệt toàn bộ kẻ địch mà không bị phát hiện. Có thể nói rằng Far Cry 3 là tập hợp của rất nhiều điều vui nhộn và sáng tạo mà bạn chưa từng biết đến trước đây.\n</p>');
INSERT INTO `news` (`new_id`, `new_title`, `new_quote`, `new_content`, `new_image`, `new_day`, `new_type_id`, `new_type`, `new_content_detail_2`) VALUES
(12, 'Gears of War: Judgment - Sự phán xét', 'Gears of War: Judgment - Sự phán xét', 'Sau thành công của ba phần đầu tiên trong vòng 7 năm kể từ khi XBox360 ra mắt, liệu Gears of War có còn thật sự hấp dẫn người chơi hay không?', 'gow_1.jpg', '15/05/2013', 1, 'news', '<p>\nGears of War có lẽ không phải là trường hợp hiếm hoi trong lịch sử của ngành công nghiệp game, khi mà ba phần của series game đầy tính sử thi này đều rất thành công và gây dựng được một lượng đông đảo fan vẫn không muốn nói lời chia tay với trò chơi yêu thích của mình. Cả hai nhà sản xuất Epic Games và People Can Fly cùng Microsoft đều đã tuyên bố: "Sẽ còn rất nhiều yếu tố trong cốt truyện có thể khai thác được, cụ thể hơn đó là quay trở lại thời điểm khi mà mọi chuyện mới bắt đầu".\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gow_1.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nĐiểm mới nhất của Judgment mà người chơi có thể nhận ra là hệ thống Declassified Mission giúp cho game thủ biết chi tiết hơn về nội dung câu chuyện. Bắt đầu ở mỗi section nhỏ, game thủ có quyền lựa chọn "giải mật" lời khai, giúp họ tiết lộ thêm thông tin về những sự kiện đã xảy ra. Khi lựa chọn "Declassify", người chơi sẽ có thêm những thách thức, nhiệm vụ mới được thêm vào, có thể làm thay đổi nội dung của section đang diễn ra.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gow_2.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nTham gia co-op trong game là một điều khá thú vị, đồng thời cũng khá dễ dàng. Người chơi có thể tham gia kể cả khi câu chuyện đã đi đến giữa chừng. Một phần tạo nên sự thú vị nêu trên có lẽ phải nhờ vào cấu trúc phân đoạn của từng chiến dịch giúp cho người chơi có thể nhanh chóng bám sát cốt truyện. Còn đối với những người chơi solo, AI của đồng minh khiến game thủ khá bực mình khi khả năng nhắm bắn quá thấp, chỉ chăm chăm vào việc chạy loanh quanh và ẩn nấp, được cái chúng có thể cứu thương cho game thủ rất nhanh chóng mỗi khi bị cạn máu, đây là điều khá cần thiết trong quá trình thu thập sao của game thủ vì dẫu sao game cũng đề cao về việc hành động nhóm hơn là hoạt động một mình.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gow_3.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nSau khi unlock được 30 ngôi sao, một nhiệm vụ ngắn hơn, truyền thống hơn – Aftermath – sẽ xuất hiện. Đây là một câu chuyện nhỏ ngoài lề xảy ra cùng lúc với các sự kiện của Gears of War 3, lúc này Braid trở về vịnh Halvo để tìm bằng được một chiếc thuyền. Dù chỉ ngắn gọn, nhưng Aftermath vẫn là phần khá hấp dẫn khi diễn ra trong bầu không khí kinh dị và đưa ra một cái kết hoàn chỉnh cho câu chuyện của Kilo Squad.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gow_4.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nCó thể nói, dù vẫn còn vài nhược điểm, Gears of War Judgment vẫn là một game hay, giữ lại được nhiều giá trị truyền thống tạo nên thành công của cả series mà không đi vào lối mòn như nhiều sản phẩm cùng thể loại.\n<br />\nTuy vậy, cốt truyện chưa thực sự xuất sắc, tiết tấu nhanh hơi khó nắm bắt phần nào cũng khiến game giảm mất độ hay của mình. Dù vậy đây vẫn là một trong những game đáng chơi nhất của tháng ba năm nay, bên cạnh các siêu phẩm khác như God of War: Ascension, Tomb Raider hay Bioshock Infinite chuẩn bị ra mắt.\n<br />\n<b>Ưu điểm :</b>\n<br />\n- Các trận chiến diễn ra nhanh chóng và linh hoạt.<br />\n- Hệ thống vũ khí và kĩ năng mới.<br />\n- Chế độ Multiplayer tốt.\n<br />\n<b>Nhược điểm :</b>\n<br />\n- Cốt truyện chưa thực sự thu hút người chơi.<br />\n- AI nhân vật không tốt lắm.<br />\n</p>'),
(13, 'Quỹ nhân ái VTC GAME : " Sống trên đời cần có một tấm lòng "', 'Quỹ nhân ái VTC GAME : " Sống trên đời cần có một tấm lòng "', 'Với ý nghĩa đó Quỹ Nhân Ái VTC Game ra đời với tiêu chí đem lại niềm vui, hi vọng sống cho những hoàn cảnh khó khăn trên mọi miền đất nước. Quỹ Nhân Ái VTC Game đã và đang ngày càng có nhiều những hoạt động thiện nguyện vô cùng ý nghĩa. ', 'gamelife_3.jpg', '16/05/2013', 7, 'gamelifes', '<br />\n<img src="http://localhost/CIF2/public/images/image-new/gamelife_3.jpg" />\n<p style="text-align:center; color:#333">Đại diện Quĩ Nhân Ái VTC Game thăm và tặng món quà 50 triệu đồng cho bệnh nhân Lê Thị Thanh</p>\n<br />\n<p>\nVà trong ngày 25/4/2013, khi nhận được những thông tin về hoàn cảnh đáng thương của em Lê Thị Thanh mắc bệnh tim, bệnh tình ngày càng xấu nếu không có 50 triệu cho chi phí phẫu thuật thì tính mạng của em sẽ bị đe dọa nghiêm trọng. Đại diện của Quỹ Nhân Ái VTC Game đã “hỏa tốc” ngay trong ngày, đến thăm và trao tặng cho em số tiền viện phí 50 triệu đồng để cho em có cơ hội mổ tim, kịp thời cứu mạng sống cho em.\n<br /> \nKhông chỉ mắc phải căn bệnh tim trầm trọng mà hoàn cảnh của em Lê Thị Thanh cũng éo le vô cùng. Có bố, có mẹ nhưng cô bé Lê Thị Thanh chẳng khác nào phận “mồ côi” khi chỉ có một mình chiến đấu với tử thần để giành giật lại sự sống mong manh. 12 tuổi thì em có đến tận 9 năm phải sống một mình với bà nội già yếu vì lí do bố mẹ đường ai nấy đi và đều đã có gia đình riêng. Gia đình em thuộc diện hộ nghèo với gia cảnh cực kỳ khó khăn. \n \nỞ lứa tuổi của em đáng ra em phải được đến trường, được vui chơi và học tập như bao bạn đồng trang lứa khác thế nhưng giờ đây em phải nằm trong viện chiến đấu từng giây từng phút với căn bệnh tim quái ác đang hành hạ mình.\n<br /> \nCảm thương trước hoàn cảnh khó khăn của em, Quỹ Nhân Ái VTC Game đã bày tỏ ý nguyện của tất cả CBCNV phía công ty muốn cứu sống bé Thanh bằng mọi giá. Số tiền 50 triệu đồng đã được đóng trực tiếp viện phí cho em theo mã số bệnh nhân để thuận lợi trong việc điều trị. Thay mặt cho bệnh viện, điều dưỡng Nguyễn Xuân Vinh gửi lời cám ơn đến các CBCNV của VTC Intecom đã có những đóng góp ý nghĩa giúp bệnh nhân Lê Thị Thanh. \n<br /> \nHiện tại tình hình sức khỏe của em Thanh vẫn đang nguy kịch và được hỗ trợ toàn bộ thở máy. Không nói được, tuy nhiên khi chứng kiến những anh, chị đến từ Quĩ Nhân Ái VTC Game đến thăm hỏi, ánh mắt của cô bé ánh lên niềm vui và hi vọng. Quá xúc động và bất ngờ trước tấm lòng của mọi người giành cho con gái, anh Lê Trường Yên (bố của Thanh) gửi lời cám ơn trân thành đến Quỹ Nhân Ái VTC Game đã đem lại cho em những  hi vọng sống vô cùng quý giá.\n</p\n\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gamelife_4.jpg" />\n<p style="text-align:center; color:#333">Các Bác sỹ và Y tá đang trao đổi với đại diện Quĩ Nhân Ái VTC Game </p>\n<br />\n<p>\nTại đây, đại diện Quĩ Nhân Ái VTC Game cũng chúc tốt đẹp cán bộ y bác sỹ BV Việt Đức cùng gia đình và em Lê Thị Thanh mau chóng lành bệnh, trở thành một công dân có ích cho xã hội. Xin gửi lời cảm ơn chân thành tới tất cả các nhân viên VTC Intecom đã chia sẻ khó khăn mong em lạc quan, yêu đời và trên môi luôn nở nụ cười tươi. Qua những chương trình này, trong thời gian tới Quỹ Nhân Ái VTC Game mong muốn trở thành nơi chia sẻ những khó khăn cho những hoàn cảnh khó khăn trên mọi miền tổ quốc.\n</p>'),
(14, ' Xóa sổ băng cướp sống trong nghĩa địa nghiện games và ma túy đá', ' Xóa sổ băng cướp sống trong nghĩa địa nghiện games và ma túy đá', 'Ngày 27/4, Công an huyện Bình Chánh đã hoàn tất hồ sơ tạm giữ 5 đối tượng gồm: Nguyễn Tấn Tài (18 tuổi, ngụ quận 3, tạm trú quận Bình Tân), Huỳnh Nhật Viên (19 tuổi, quê quán Bình Định), Nguyễn Văn Tú (24 tuổi, sống lang thang), Dương Thị Diễm Châu (17 tuổi, quê quán An Giang), Nguyễn Thành Hoàng (18 tuổi, quê quán Long An) để điều tra và xử lý về hành vi “cướp tài sản”.', 'gamelife_1.jpg', '12/05/2013', 7, 'gamelifes', '<p />\nTheo nguồn tin mà các trinh sát nắm được, băng nhóm do Tài cầm đầu có khoảng 5 - 6 đối tượng sống lang thang và thường chọn nghĩa trang làm nơi trú ngụ. Các đối tượng này nghiện ma túy đá nặng và rất thích cày game. Khoảng 4h ngày 25/4, một tổ trinh sát phát hiện một số đối tượng đang đánh anh Nguyễn Thành Long (26 tuổi, ngụ Quảng Nam, tạm trú huyện Bình Chánh) hòng cướp chiếc xe gắn máy liền xông đến can thiệp.\n<br />\nPhát hiện có người, các đối tượng lên xe rồ ga bỏ chạy bạt mạng. Các trinh sát truy đuổi đến trước số nhà: E1/2H1 Vĩnh Lộc A, ấp 5, xã Vĩnh Lộc A đã áp sát một đối tượng. Đối tượng này đã bỏ xe lại hiện trường và bỏ trốn. Cuộc vây bắt kéo dài đến 10h cùng ngày thì đối tượng Nguyễn Thành Hoàng bị bắt khi đang lẩn trốn tại quận Tân Phú.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gamelife_1.jpg" />\n<p style="text-align:center; color:#333">Các đối tượng trong vụ án</p>\n<p>\nTừ lời khai của Hoàng, Công an Bình Chánh đã triển khai lực lượng và đến chiều 25/4 tất cả các đối tượng đã bị bắt. Các đối tượng khai nhận đã gây ra 12 vụ trộm cướp tài sản chủ yếu là người đi đường, tài sản của những người say xỉn ngủ trên lề đường và đột nhập vào nhà dân trộm tài sản.\n<br />\nHằng ngày các đối tượng này “ngồi đồng” trong quán games và đợi 12 giờ khuya mới ra tay thực hiện hành vi cướp giật. Khi cướp được tài sản, các đối tượng đem bán chia nhau mua ma túy đá sử dụng. Công an huyện Bình Chánh đang tiến hành mở rộng điều tra băng nhóm cướp tài sản trên để xử lý\n</p>\n<br />'),
(15, 'Trẻ Nghiện Game – Lỗi Tại Gia Đình', 'Trẻ Nghiện Game – Lỗi Tại Gia Đình', 'Cứ ngỡ cho trẻ em chơi game để thư giãn, nhưng đến lúc biết con mình đã “nghiện”… game thì nhiều phụ huynh mới vỡ lẽ. Những hậu quả từ game trong thời gian qua đã gióng lên hồi chuông báo động. Trong đó, sự nuông chìu, thiếu quan tâm của bậc làm cha mẹ là một trong những nguyên nhân vô tình đẩy trẻ vào những hệ lụy đau lòng…', 'gamelife_5.jpg', '22/05/2013', 7, 'gamelifes', '<p>\n<b>Từ những game trên điện thoại di động (ĐTDĐ)…</b>\n<br />\nThời gian qua, tình trạng trẻ ghiền trò chơi trên ĐTDĐ đã trở thành nỗi lo chung của nhiều bậc phụ huynh. Không ít vụ việc đáng tiếc xảy ra với trẻ em chỉ vì trót vướng vào “con ma” game.\n<br />\nChị H.N, tiểu thương chợ Trần Huỳnh, chia sẻ: “Vì không có người trông nom nên tôi cho con “làm bạn” với chiếc ĐTDĐ. Cứ cầm điện thoại là nó chơi game. Lúc đầu cứ tưởng cho con chơi để giải trí, nhưng đâu ngờ nó mê tới vậy. Có khi cho chơi, nó mới chịu ăn, chịu ngủ. Vậy là tôi phải chìu con… Lâu ngày rồi thành thói quen”.\n<br />\nMột trường hợp khác, vì không được cha mẹ quan tâm do bận việc nên em học sinh này đã tìm đến với game. Lâu ngày, em rơi vào tình trạng trầm cảm, sống “thu mình” trước đám đông. Mặc dù được cha mẹ đưa đi điều trị ở TP. Hồ Chí Minh nhưng kết quả cũng không mấy khả quan…\n<br />\nLại có ý kiến cho rằng, để con trẻ tiếp xúc sớm với ĐTDĐ là để chúng có cơ hội làm quen với những công nghệ mới, ứng dụng hay. Đồng thời, cho trẻ giải trí và kích thích việc học tập. Tuy nhiên, điều đáng nói là những đứa trẻ này tuổi đời còn nhỏ, các em chưa biết chọn lọc những thứ mà cha mẹ chúng cho là bổ ích để thu nạp, đổi lại các em có dịp “chạm mặt” quá sớm với những game có nội dung bạo lực, không lành mạnh. Chính môi trường của thế giới trong game đã làm cho những tâm hồn trong sáng như tờ giấy của trẻ bị những vết “trầy xước” không đáng có.\n<br />\n<b>… Đến mạng Internet và những hệ lụy</b>\n<br />\nViệc cho trẻ tiếp xúc sớm với những trò chơi trên ĐTDĐ là “bàn đạp” đưa trẻ vào một thế giới lầm lạc. Sau những trò chơi nho nhỏ trên ĐTDĐ, trẻ bắt đầu bước vào một cuộc sống ảo luôn có những “cái bẫy” đang chực chờ.\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gamelife_5.jpg" />\n<p style="text-align:center; color:#333">Ăn uống, sinh hoạt ngay tại chổ chơi game</p>\n<br />\n<p>\nVì muốn con có điều kiện học tập tốt, nhiều phụ huynh đã sẵn sàng mua máy vi tính cho con. Anh L.V.T. (phường Nhà Mát, TP. Bạc Liêu) là một trường hợp như thế. Vì không muốn hai đứa con mình phải la cà tiệm game nên anh T. đã mua cho con máy vi tính và kết nối mạng Internet. Học tập, tra cứu tài liệu đâu không thấy, nhưng việc con anh dán mắt vào màn hình máy tính để thỏa sức với game là điều dễ bắt gặp nhất. Thậm chí, có những trường hợp các em (vô tình hay cố ý) truy cập vào những trang web có nội dung đồi trụy. Và lẽ đương nhiên, những tâm hồn non nớt ấy đã bị đầu độc!\n<br />\nChúng ta không còn lạ lẫm với hình ảnh những chiếc áo trắng học trò xuất hiện tại các phòng game. Tại đây, những “game thủ nhí” tỏ ra rất nhập vai vào các nhân vật trên màn hình và kèm theo đó những câu văng tục. Có em còn “chưa kịp” thay đồng phục, có em đôi mắt lờ đờ vì đã ngồi hàng giờ trên máy tính và không thiếu những em vô tư hút thuốc lá một cách điệu nghệ… Còn chủ phòng game chỉ vì lợi nhuận mà chẳng quan tâm đến độ tuổi và sẵn sàng đáp ứng khi các bạn nhỏ có nhu cầu chơi game. Rồi đây, con đường tương lai của các em sẽ bị trượt dài bởi những thứ không lành mạnh đã tiêm nhiễm trong tâm trí. Nếu không kịp thời ngăn chặn thì những thước phim mang nội dung: lừa cha dối mẹ, trộm cắp, trốn học, đánh giết nhau, tự tử… sẽ đầu độc các em!\n</p>\n<br />\n<img src="http://localhost/CIF2/public/images/image-new/gamelife_6.jpg" />\n<p style="text-align:center; color:#333">Noi dung anh 1</p>\n<br />\n<p>\nTừ những vấn nạn trên, toàn xã hội nói chung và các bậc làm cha mẹ nói riêng cần quan tâm hơn đến sinh hoạt, học tập của con em mình, để trẻ được lớn lên trong một môi trường lành mạnh. Hãy để những tâm hồn ngây thơ ấy được chở che bởi tình thương, sự quan tâm đúng cách của người lớn.\n</p>'),
(100, 'Game thủ phát hoảng trước thử thách quá khó', 'Game thủ phát hoảng trước thử thách quá khó', 'Chuỗi sự kiện trong Nhật Nguyệt Thần Kiếm khó khăn nhất là thu thập đủ bộ Thần Trang Cực Phẩm.', 'new_1.jpg', '22/06/2013', 2, 'news', '<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Với 7 ng&agrave;y t&iacute;nh từ khi m&aacute;y chủ ra mắt, 7 m&oacute;n trong set Thần Trang Cực Phẩm sẽ gi&agrave;nh tặng người chơi đạt thứ hạng cao nhất ở hạng mục: Cấp độ, đấu trường, chiến lực, kinh mạch, linh th&uacute;, ti&ecirc;u hao v&agrave;ng v&agrave; tọa kỵ.</p>\r\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="445">\r\n	<tbody>\r\n		<tr>\r\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				<img border="1" height="271" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/F0/Cuc_Pham.jpg" width="480" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				Thử th&aacute;ch được đ&aacute;nh gi&aacute; l&agrave; &quot;kinh dị&quot; nhất m&agrave; NPH d&agrave;nh cho người chơi</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Người chơi&nbsp;<b>Xihaae</b>&nbsp;m&aacute;y chủ Thi&ecirc;n Địa cho biết: &quot;M&igrave;nh d&aacute;m khẳng định kh&ocirc;ng một game thủ n&agrave;o c&oacute; thể &#39;&ocirc;m&#39; hết được giải thưởng sự kiện n&agrave;y. Để l&ecirc;n Top 1 hạng mục đ&atilde; kh&oacute;, d&ugrave; bạn c&oacute; tiền nhiều th&igrave; cũng nằm ở sự h&ecirc;n xui v&igrave; trong đấu trường, kh&ocirc;ng phải cứ chiến lực lớn hơn l&agrave; thắng v&igrave; c&ograve;n do t&iacute;nh chất class nh&acirc;n vật. C&oacute; l&agrave; si&ecirc;u si&ecirc;u nh&acirc;n cũng kh&ocirc;ng thể giữ vị tr&iacute; Top của mọi hạng mục được!&quot;.</p>\r\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="250">\r\n	<tbody>\r\n		<tr>\r\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				<img border="1" height="273" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/F0/Thantrang1.jpg" width="480" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				Một số h&igrave;nh ảnh về c&aacute;c m&oacute;n trong bộ Thần Trang Cực Phẩm</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p align="left" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Đại diện NPH cho biết: &quot;Thường c&aacute;c NPH game hiện nay hay chọn đua Top theo một khoảng thời gian n&agrave;o đ&oacute;. Với<em>Nhật Nguyệt Thần Kiếm</em>, ch&uacute;ng t&ocirc;i mong muốn sự ganh đua, cuốn h&uacute;t trong suốt 7 ng&agrave;y li&ecirc;n tục. Sự kiện n&agrave;y ch&uacute;ng t&ocirc;i cũng quyết định t&iacute;ch hợp trực tiếp v&agrave;o trong game thay v&igrave; tổ chức chạy ngo&agrave;i như c&aacute;c sự kiện kh&aacute;c. Thử th&aacute;ch n&agrave;y đ&uacute;ng l&agrave; rất kh&oacute;. Tuy nhi&ecirc;n, cơ hội cần chia đều cho mọi người chơi&quot;.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Cũng theo th&ocirc;ng tin từ NPH game, với sự ổn định như hiện nay, giai đoạn Open Beta sẽ mở sớm v&agrave;o ng&agrave;y 17/5 tới.</p>\r\n<p align="right" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	<strong>Ho&agrave;ng Minh</strong></p>\r\n'),
(101, 'Diablo III - chặng đường một năm nhìn lại', 'Diablo III - chặng đường một năm nhìn lại', '15/5/2012 là ngày mà cánh cửa địa ngục mở toang và trò chơi bom tấn của hãng Blizzard đã vượt qua ranh giới, xuất hiện trên thế giới này một cách đầy kiêu hãnh.', 'new_2.jpg', '19/05/2013', 1, 'news', '<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Kể từ đ&oacute; tới nay h&agrave;ng triệu anh h&ugrave;ng đ&atilde; được tạo ra, h&agrave;ng tỷ giờ đ&atilde; được &quot;thi&ecirc;u đốt&quot; trong tr&ograve; chơi v&agrave; h&agrave;ng ngh&igrave;n tỷ y&ecirc;u qu&aacute;i, quỷ ma đ&atilde; bị ti&ecirc;u diệt kh&ocirc;ng thương tiếc.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Nếu như fan h&acirc;m mộ của&nbsp;<em>Call of Duty, Battlefield, Assassin&#39;s Creed...</em>&nbsp;chỉ cần đợi một năm hay nhiều l&agrave; 2-3 năm để chờ đ&oacute;n sản phẩm mới của m&igrave;nh th&igrave; với cộng đồng game thủ h&acirc;m mộ d&ograve;ng&nbsp;<em>Diablo</em>, những người chơi n&agrave;y đ&atilde; phải chờ hơn 10 năm mới c&oacute; được cơ hội chạm tay v&agrave;o b&igrave;a đĩa phi&ecirc;n bản&nbsp;<em>Diablo III.</em></p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Đ&uacute;ng 1 năm trước đ&acirc;y, 15/5/2012, h&agrave;ng triệu game thủ tr&ecirc;n khắp thế giới đ&atilde;&nbsp;<a class="Normal" href="http://gamethu.vnexpress.net/gt/hinh-anh/hinh-game-thu/2012/05/25713-diablo-iii-duoc-don-cho-tren-khap-the-gioi/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><strong><font color="#0080ff">xếp h&agrave;ng d&agrave;i trước c&aacute;c cửa h&agrave;ng</font></strong></a>, qu&ecirc;n ăn qu&ecirc;n uống để chầu chực, chen lấn nhau với hi vọng m&igrave;nh l&agrave; người đầu ti&ecirc;n được sở hữu si&ecirc;u phẩm tiếp theo của Blizzard.</p>\r\n<table align="center" border="0" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img alt="Đối với nhiều game thủ, ngày này một năm trước có rất nhiều kỷ niệm." border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/D3/diablo_iii.jpg" width="480" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				Đối với nhiều game thủ, ng&agrave;y n&agrave;y một năm trước c&oacute; rất nhiều kỷ niệm.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Sau thời khắc ph&aacute;t h&agrave;nh, cư d&acirc;n mạng tiếp tục s&ocirc;i sục với những biến động đầu ti&ecirc;n của người chơi như lỗi qu&aacute; tải server (<a class="Normal" href="http://gamethu.vnexpress.net/gt/diem-tin/2012/05/25734-hai-huoc-voi-thong-bao-loi-qua-tai-server-trong-diablo-iii/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><strong><font color="#0080ff">Error 37</font></strong></a>) &quot;huyền thoại&quot;, chen ch&uacute;c trong những m&aacute;y chủ đầu ti&ecirc;n, đua nhau xem ai l&agrave; người ph&aacute; đảo game nhanh nhất thế giới. Độ n&oacute;ng từ kh&ocirc;ng kh&iacute; đ&oacute;n nhận tr&ograve; chơi n&agrave;y xem ra c&ograve;n vượt xa cả những gi&aacute; trị hấp dẫn m&agrave; n&oacute; mang lại, bởi hiệu ứng th&ocirc;ng tin về cuộc chiến bảo vệ th&aacute;nh địa Sanctuary lu&ocirc;n khiến c&aacute;c fan h&acirc;m mộ h&agrave;o hứng.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	<em>Diablo III</em>&nbsp;mang trong m&igrave;nh c&ocirc;ng nghệ sản xuất hiện đại, ti&ecirc;n tiến với sự bổ sung của nhiều t&iacute;nh năng đặc biệt, chế độ chơi mới lạ, hấp dẫn đ&atilde; được cả đội ngũ ph&aacute;t triển đồ sộ của h&atilde;ng Blizzard t&iacute;nh to&aacute;n, ph&acirc;n t&iacute;ch v&agrave; nghi&ecirc;n cứu trong nhiều năm. Ngo&agrave;i đồ họa ấn tượng, t&iacute;nh năng đấu gi&aacute; bằng tiền mặt cũng việc &eacute;p buộc game thủ lu&ocirc;n phải online cũng tạo ra nhiều sự thay đổi trong lối chơi truyền thống.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Tuy nhi&ecirc;n, tr&ograve; chơi cũng kh&ocirc;ng tr&aacute;nh khỏi những ch&ecirc; bai khi game thủ cho rằng h&atilde;ng ph&aacute;t triển đ&atilde; l&agrave;m mất đi gameplay độc đ&aacute;o của phi&ecirc;n bản trước, tạo ra một sản phẩm với qu&aacute; nhiều thay đổi. Một số kh&aacute;c lại cho rằng<em>Diablo III</em>&nbsp;đ&atilde; bị đơn giản h&oacute;a qu&aacute; nhiều, cảm gi&aacute;c săn đồ kh&ocirc;ng c&ograve;n th&iacute;ch th&uacute;, m&agrave;n chơi qu&aacute; ngắn. Rất nhiều c&aacute;c cuộc tranh luận, b&agrave;n c&atilde;i v&agrave; cả &quot;ch&eacute;m gi&oacute;&quot; với nhau giữa hai phe ph&aacute;i ủng hộ v&agrave; anti-fan&nbsp;<em>Diablo III.</em></p>\r\n<table align="center" border="0" cellpadding="0" cellspacing="0" width="1">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<em style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);"><img alt="Diablo III đã tròn một tuổi." border="1" height="336" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/D3/happy-birthday-diablo.jpg" width="480" /></em></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79); text-align: center;">\r\n				<em style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">Diablo III đ&atilde; tr&ograve;n một tuổi.</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Sau khoảng thời gian đầu ho&agrave;nh tr&aacute;ng, tr&ograve; chơi bắt đầu ch&igrave;m xuống để nhường lối cho c&aacute;c d&ograve;ng game kh&aacute;c đến thời điểm ph&aacute;t h&agrave;nh như&nbsp;<em>FIFA 13, PES 2013, Call of Duty: Black Ops II, Assassin&#39;s Creed III...</em>&nbsp;Dẫu vậy, sự ch&uacute; &yacute; tới tr&ograve; chơi bom tấn n&agrave;y vẫn chưa hề nguội lạnh khi đ&acirc;u đ&oacute;, chuyện&nbsp;<em>&quot;</em><a class="Normal" href="http://gamethu.vnexpress.net/gt/diem-tin/2012/06/26143-blizzard-voi-tien-game-thu-diablo-iii/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><font color="#0080ff">Blizzard &#39;v&ograve;i tiền&#39; game thủ&nbsp;<em>Diablo III</em></font></a><em>&quot;</em>&nbsp;vẫn thu h&uacute;t sự quan t&acirc;m, vấn đề nh&agrave; đấu gi&aacute; bị lỗi, nh&agrave; ph&aacute;t triển đưa ra&nbsp;<a class="Normal" href="http://gamethu.vnexpress.net/gt/dang-choi/2012/06/26207-cai-chet-vinh-vien-trong-diablo-iii/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><font color="#0080ff">chế độ chơi si&ecirc;u kh&oacute;</font></a>...</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	H&atilde;ng Blizzard vẫn đều đặn cung cấp cho người chơi c&aacute;c bản cập nhật h&agrave;ng th&aacute;ng,&nbsp;<a class="Normal" href="http://gamethu.vnexpress.net/gt/hinh-anh/hinh-game/2012/12/29877-cosplay-demon-hunter-trong-diablo-iii-doc-dao/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><font color="#0080ff">cosplay nh&acirc;n vật trong&nbsp;<em>Diablo III</em></font></a>&nbsp;hầu như chẳng bao giờ vắng mặt tại c&aacute;c lễ hội hay sự kiện cosplay, những bức fan-art của sản phẩm n&agrave;y được người h&acirc;m mộ vẽ h&agrave;ng ng&agrave;y, h&agrave;ng giờ tr&ecirc;n khắp thế giới. Trong b&aacute;o c&aacute;o kinh doanh mới đ&acirc;y của Blizzard, doanh thu từ&nbsp;<em>Diablo III</em>&nbsp;vẫn chiếm phần trăm kh&ocirc;ng nhỏ trong tổng doanh thu từ game của g&atilde; khổng lồ. Người chơi vẫn tiếp tục mua đĩa game&nbsp;<em>Diablo III</em>&nbsp;để một lần được đối mặt với ch&uacute;a quỷ, nhiều game thủ vẫn t&iacute;ch c&oacute;p tiền bạc để được một lần sở hữu &quot;bom tấn&quot; n&agrave;y cho ri&ecirc;ng m&igrave;nh, c&aacute;c chuy&ecirc;n gia ph&acirc;n t&iacute;ch lẫn marketting của ng&agrave;nh c&ocirc;ng nghiệp game vẫn phải &quot;nghi&ecirc;ng m&igrave;nh k&iacute;nh phục&quot; trước th&agrave;nh c&ocirc;ng m&agrave; Blizzard đ&atilde; tạo dựng được. Rất nhiều h&atilde;ng ph&aacute;t triển game mọi nơi đ&atilde; &quot;mượn&quot; &yacute; tưởng, đồ họa, gameplay, nh&acirc;n vật hay đơn giả chỉ l&agrave; thương hiệu của tr&ograve; chơi n&agrave;y để đưa v&agrave;o sản phẩm của m&igrave;nh nhằm thu h&uacute;t sự ch&uacute; &yacute; từ cộng đồng.</p>\r\n<table align="center" border="0" cellpadding="0" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img alt="Những nhân vật huyền thoại của dòng game Diablo sẽ sống mãi trong trái tim các fan hâm mộ." border="1" height="477" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/D3/diablo.jpg" width="480" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79); text-align: center;">\r\n				Những nh&acirc;n vật huyền thoại của d&ograve;ng game Diablo sẽ sống m&atilde;i trong tr&aacute;i tim c&aacute;c fan h&acirc;m mộ.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Sau một năm, với nhiều ch&iacute;nh s&aacute;ch khuyến m&atilde;i v&agrave; trợ gi&aacute; từ c&aacute;c h&atilde;ng b&aacute;n lẻ, game thủ giờ đ&acirc;y c&oacute; thể bớt đắn đo hơn trong việc bỏ tiền ra mua một đĩa game nguy&ecirc;n bản. Nhiều game thủ cũng đ&atilde; c&oacute; thể kiếm tiền đều đặn qua việc b&aacute;n đồ trong game.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Kh&ocirc;ng dừng lại ở việc &acirc;m thầm h&acirc;m n&oacute;ng cộng đồng tr&ecirc;n diễn đ&agrave;n, fix lỗi, b&aacute;n game v&agrave; đếm tiền từ doanh số, h&atilde;ng ph&aacute;t triển vẫn tiếp tục &acirc;m thầm cải tiến v&agrave; tới đ&acirc;y sẽ ph&aacute;t h&agrave;nh phi&ecirc;n bản game tr&ecirc;n hệ m&aacute;y console (cả PS3 v&agrave; PS4) nhằm mang tới những trải nghiệm mới khi&nbsp;<a class="Normal" href="http://gamethu.vnexpress.net/gt/dang-choi/2013/03/31815-trailer-dau-tien-cua-diablo-iii-tren-console-da-xuat-hien/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><font color="#0080ff">điều khiển nh&acirc;n vật bằng tay cầm điều khiển</font></a>.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Chắc chắn sẽ c&ograve;n rất l&acirc;u, rất l&acirc;u nữa c&aacute;c fan h&acirc;m mộ mới c&oacute; thể nghe được th&ocirc;ng tin về phi&ecirc;n bản tiếp theo<em>Diablo IV</em>. 3 năm, 5 năm, 10 năm hoặc thậm ch&iacute; l&agrave; l&acirc;u hơn nữa. Tuy nhi&ecirc;n, kh&ocirc;ng v&igrave; thế m&agrave;&nbsp;<em>Diablo III</em>&nbsp;bị đ&aacute;nh gi&aacute; thấp k&eacute;m đi hay số lượng fan h&acirc;m mộ của tr&ograve; chơi n&agrave;y giảm bớt. Sản phẩm của Blizzard đ&atilde; trở th&agrave;nh tượng đ&agrave;i trong ng&agrave;nh c&ocirc;ng nghiệp game hiện đại, v&agrave; khi đ&atilde; trở th&agrave;nh một tượng đ&agrave;i trong t&acirc;m tr&iacute; game thủ, n&oacute; sẽ kh&ocirc;ng thể thay thế hoặc ph&aacute; bỏ.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Trong tuần n&agrave;y, h&atilde;ng ph&aacute;t triển sẽ tổ chức h&agrave;ng loạt sự kiện, bổ sung th&ecirc;m t&iacute;nh năng, c&aacute;c cuộc thi cho người h&acirc;m mộ nhằm kỷ niệm 1 năm sinh nhật của game. Người chơi c&oacute; thể theo d&otilde;i tại trang web ri&ecirc;ng<a class="Normal" href="http://diablo.somepage.com/" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-decoration: none;"><strong><font color="#0080ff">&nbsp;tại đ&acirc;y.</font></strong></a></p>\r\n<p align="right" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	<strong>Bảo Nam</strong></p>\r\n'),
(103, 'Rò rỉ diện mạo các nhân vật mới của Call of Duty: Ghosts', 'Rò rỉ diện mạo các nhân vật mới của Call of Duty: Ghosts', 'Cả 5 hình ảnh mới về các nhân vật chính đều có điểm chung là khuôn mặt được che phủ bởi một thứ gì đó.', 'new_3.jpg', '19/06/2013', 1, 'news', '<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Ch&iacute;nh thức ph&aacute;t h&agrave;nh v&agrave;o ng&agrave;y 5/11/2013,&nbsp;<i>Ghosts&nbsp;</i>được studio gạo cội - cha đẻ của d&ograve;ng game&nbsp;<em>Call of Duty</em>&nbsp;- Infinity Ward thực hiện. Game sẽ c&oacute; mặt tr&ecirc;n c&aacute;c hệ m&aacute;y PC, PS3, Xbox360 v&agrave; thế hệ console tiếp theo, hệt như c&aacute;c fanpage v&agrave; trang tin đ&atilde; dự đo&aacute;n từ trước. WiiU kh&ocirc;ng được đả động đến, nhưng điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa sản phẩm sẽ kh&ocirc;ng xuất hiện tr&ecirc;n hệ m&aacute;y n&agrave;y.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Theo như Activision,&nbsp;<i>Ghosts&nbsp;</i>sẽ &quot;đem tới một trải nghiệm ho&agrave;n to&agrave;n mới dựa tr&ecirc;n cốt truyện, bối cảnh v&agrave; nh&acirc;n vật được x&acirc;y dựng lại ho&agrave;n to&agrave;n, tất cả được thực hiện tr&ecirc;n thế hệ engine mới nhất của d&ograve;ng&nbsp;<em>Call of Duty</em>&nbsp;nhằm định nghĩa lại to&agrave;n bộ series&hellip;&quot;</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	X&aacute;c nhận những th&ocirc;ng tin r&ograve; rỉ tuần trước từ một hệ thống b&aacute;n lẻ ở Anh, Activison đ&atilde; đưa ra box art v&agrave; logo ch&iacute;nh thức của game, đồng thời tr&ecirc;n k&ecirc;nh&nbsp;<i>Call of Duty</i>&nbsp;ở Youtube cũng xuất hiện đoạn teaser trailer ngắn k&eacute;o d&agrave;i hơn 1 ph&uacute;t. C&ugrave;ng ng&agrave;y, trang Facebook ch&iacute;nh thức của&nbsp;<i>Call of Duty: Ghosts&nbsp;</i>bắt đầu hoạt động.</p>\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">\r\n	<img align="center" border="1" height="677" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/48/E3/Call-of-Duty-Ghosts-X360-610x860.jpg" width="480" /></p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	&quot;Trước nay, mọi người đều mong ch&uacute;ng t&ocirc;i thực hiện&nbsp;<i>Modern Warfare 4</i>, đ&oacute; l&agrave; sự lựa chọn kh&aacute; an to&agrave;n&quot;, điều h&agrave;nh sản xuất của Infinity Ward, &ocirc;ng Mark Rubin cho biết.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	&quot;Tuy nhi&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng dừng lại sau những th&agrave;nh c&ocirc;ng đ&atilde; đạt được. Sự xuất hiện của d&ograve;ng console thứ t&aacute;m ch&iacute;nh l&agrave; cơ hội tốt để bắt đầu một chương mới cho&nbsp;<i>Call of Duty</i>. V&igrave; thế ch&uacute;ng t&ocirc;i đ&atilde; bắt tay v&agrave;o l&agrave;m một d&ograve;ng sản phẩm ho&agrave;n to&agrave;n mới, một engine mới c&ugrave;ng rất nhiều &yacute; tưởng v&agrave; trải nghiệm kh&aacute;c nhau cho người chơi. Thật l&agrave; kh&oacute; để chờ đến l&uacute;c chia sẻ những điều n&agrave;y với cộng đồng y&ecirc;u th&iacute;ch d&ograve;ng game&quot;</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	&quot;Ch&uacute;ng t&ocirc;i lu&ocirc;n vui mừng trước những phản ứng t&iacute;ch cực từ c&aacute;c nh&agrave; ph&ecirc; b&igrave;nh v&agrave; fan h&acirc;m mộ d&ograve;ng game&nbsp;<i>Call of Duty</i>, một trong những series c&oacute; nền tảng vững chắc v&agrave; cộng đồng đ&ocirc;ng đảo nhất hiện nay tr&ecirc;n hệ m&aacute;y Xbox360&quot; Don Mattrick, trưởng bộ phận kinh doanh tương t&aacute;c giải tr&iacute; của Microsoft ph&aacute;t biểu.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	&quot;Với , ch&uacute;ng t&ocirc;i kh&ocirc;ng nghi ngờ g&igrave; về việc đối t&aacute;c l&acirc;u năm của m&igrave;nh, Infinity Ward v&agrave; Activision, sẽ đặt ra những ti&ecirc;u chuẩn cao nhất chưa từng thấy cho d&ograve;ng sản phẩm danh tiếng n&agrave;y.&quot;</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Ng&agrave;y 21/5 tới, những th&ocirc;ng tin tiếp theo về game sẽ tiếp tục được h&eacute; lộ.</p>\r\n<p align="right" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	<strong>Nguyễn H&agrave;o</strong></p>\r\n'),
(105, 'Bí ẩn Hiên Viên Kiếm được vén màn', 'Bí ẩn Hiên Viên Kiếm được vén màn', 'Đây chính là một game mobile online (gMO) mới từ NPH VTC Mobile.', 'new_4.jpg', '12/03/2013', 1, 'news', '<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	V&agrave;i tuần trở lại đ&acirc;y, sự ch&uacute; &yacute; của cộng đồng đ&atilde; v&agrave; đang đổ dồn v&agrave;o sự xuất hiện của teaser b&iacute; ẩn&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>. Nguy&ecirc;n do l&agrave; c&aacute;i t&ecirc;n của n&oacute; c&oacute; li&ecirc;n quan mật thiết đến&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm X7&nbsp;</em>- MMORPG 3D đang nổi đ&igrave;nh nổi đ&aacute;m ở Trung Quốc của Changyou. Kh&ocirc;ng &iacute;t người b&agrave;y tỏ hy vọng, dự &aacute;n b&iacute; ẩn đằng sau trang giới thiệu n&agrave;y sẽ l&agrave;&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm X7&nbsp;</em>để họ c&oacute; th&ecirc;m một lựa chọn MMO hấp dẫn nữa cho kỳ nghỉ h&egrave; sắp tới.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Trong một b&agrave;i ph&acirc;n t&iacute;ch gần đ&acirc;y nhất,&nbsp;<em>Game Thủ.net</em>&nbsp;từng đưa ra dự đo&aacute;n về việc dự &aacute;n b&iacute; ẩn n&agrave;y chỉ đơn thuần l&agrave; một gMO chứ kh&ocirc;ng phải MMORPG của Changyou. Rốt cuộc, dự đo&aacute;n đ&oacute; đ&atilde; trở th&agrave;nh sự thực khi h&ocirc;m nay 13/5, quản trị fanpage&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm&nbsp;</em>đ&atilde; đăng đ&agrave;n tuy&ecirc;n bố đ&acirc;y ch&iacute;nh l&agrave; một gMO từ VTC Mobile.</p>\r\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img alt="Lời công bố của admin trên fanpage Hiên Viên Kiếm." border="1" height="281" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/9C/hien-vien.jpg" width="480" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				Lời c&ocirc;ng bố của admin tr&ecirc;n fanpage Hi&ecirc;n Vi&ecirc;n Kiếm.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Cụ thể theo lời admin fanpage, gMO&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>&nbsp;xuất ph&aacute;t từ nội dung, bối cảnh nhiều lớp của game. &quot;Giữa năm 2012 khi VTC Mobile Game ch&iacute;nh thức ph&aacute;t h&agrave;nh gMO&nbsp;<em>Ho&agrave;ng Đế,</em>&nbsp;vốn c&oacute; t&ecirc;n gốc l&agrave;&nbsp;<em>Hi&ecirc;n Vi&ecirc;n</em>, kể về Ho&agrave;ng đế Hi&ecirc;n Vi&ecirc;n c&ugrave;ng thanh kiếm thần dẹp y&ecirc;n loạn lạc.&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm&nbsp;</em>l&agrave; phần tiếp nối của&nbsp;<em>Ho&agrave;ng Đế&nbsp;</em>với bối cảnh hậu thế truy t&igrave;m thanh kiếm của Ho&agrave;ng đế Hi&ecirc;n Vi&ecirc;n đ&atilde; bị thất lạc. Đ&acirc;y cũng ch&iacute;nh l&agrave; một trong những chiến lược của VTC Mobile Game khi ph&aacute;t triển c&aacute;c game của m&igrave;nh theo một hệ thống, c&oacute; mối li&ecirc;n quan chặt chẽ&quot;, vị quản trị cho biết.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Cũng theo th&ocirc;ng tin tr&ecirc;n fanpage,&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>&nbsp;l&agrave; gMO đề cao t&iacute;nh bang hội, khả năng kết hợp nh&oacute;m. Hiện tại game đang v&agrave;o chặng cuối của Alpha Test (test nội bộ) trước khi mở cửa Closed Beta.</p>\r\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img alt="Hiên Viên Kiếm không phải là MMORPG như một số người dự đoán." border="1" height="282" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/9C/hien-vien..jpg" width="480" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\r\n				<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>&nbsp;kh&ocirc;ng phải l&agrave; MMORPG như một số người dự đo&aacute;n.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Phản ứng trước th&ocirc;ng tin tr&ecirc;n fanpage&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm,&nbsp;</em>một số game thủ b&agrave;y tỏ sự thất vọng r&otilde; rệt do trước đ&oacute; họ từng đinh ninh đ&acirc;y ch&iacute;nh l&agrave;&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm X7 -&nbsp;</em>MMORPG 3D từ Changyou. &quot;Ặc, tưởng game client 3D ho&agrave;nh tr&aacute;ng lắm, ai ngờ l&agrave; game mobile&quot;, một game thủ nhăn nh&oacute;.</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	Trong khi đ&oacute;, một số người kh&aacute;c lại tỏ ra kh&aacute; b&igrave;nh thản khi dường như đ&atilde; đo&aacute;n trước ch&acirc;n tướng sự việc. Những người n&agrave;y li&ecirc;n tục đặt ra c&aacute;c c&acirc;u hỏi cho admin về tr&ograve; chơi như game c&oacute; h&igrave;nh ảnh ra sao, game sẽ hỗ trợ d&ograve;ng m&aacute;y n&agrave;o...</p>\r\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	<em>Game Thủ.net</em>&nbsp;sẽ tiếp tục cập nhật th&ocirc;ng tin chi tiết về dự &aacute;n n&agrave;y.</p>\r\n<p align="right" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\r\n	<strong>Ho&agrave;ng Qu&acirc;n</strong></p>\r\n');
INSERT INTO `news` (`new_id`, `new_title`, `new_quote`, `new_content`, `new_image`, `new_day`, `new_type_id`, `new_type`, `new_content_detail_2`) VALUES
(106, 'Nếu một ngày Việt Nam... không còn game online', 'Nếu một ngày Việt Nam... không còn game online', 'Khi game online hoàn toàn biến mất, bức tranh làng game sẽ ra sao?', 'new_5.jpg', '17/05/2013', 7, 'gamelifes', '<p>\n	&nbsp;</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">C&oacute; ai đ&oacute; n&oacute;i rằng, game online l&agrave; nguy&ecirc;n nh&acirc;n s&acirc;u xa của bạo lực. C&oacute; ai đ&oacute; n&oacute;i rằng, game online đang &quot;l&agrave;m hỏng&quot; một thế hệ trẻ. Lại c&oacute; quan điểm cho rằng, chơi game online gi&uacute;p thư gi&atilde;n rất tốt, gi&uacute;p cải thiện tr&iacute; nhớ v&agrave; thị gi&aacute;c...</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Nếu một ng&agrave;y game online đột nhi&ecirc;n biến mất, l&agrave;ng game n&agrave;y sẽ ra sao? Dưới đ&acirc;y l&agrave; những hệ lụy được tưởng tượng bởi một độc giả c&oacute; nickname Divedau của&nbsp;<em>Game Thủ.net</em>.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Game biến mất rồi, l&agrave;m game về đ&acirc;u...</strong></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Thời điểm hiện tại, Việt Nam c&oacute; khoảng ngo&agrave;i 20 NPH game lớn nhỏ. Cũ c&oacute; VNG, VTC Game, FPT Online, Asiasoft&hellip;, mới c&oacute; Sgame, Soha Game, Game5... Nếu kh&ocirc;ng c&oacute; game online, tất cả NPH n&agrave;y sẽ đ&oacute;ng cửa v&agrave; c&aacute;c trang tin game cũng ngừng hoạt động.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<span style="font-family:tahoma,geneva,sans-serif;"><img alt="Emobi và Sát Thát Truyền Kỳ sẽ về đâu nếu không còn game online?" border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/4A/11/khong-game-1.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\n				<span style="font-family:tahoma,geneva,sans-serif;">Emobi v&agrave; S&aacute;t Th&aacute;t Truyền Kỳ sẽ về đ&acirc;u nếu kh&ocirc;ng c&ograve;n game online?</span></td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Nhiều studio l&agrave;m game online vụt tắt d&ugrave; chưa từng một khắc huy ho&agrave;ng.V&agrave; tất yếu, v&agrave;i ng&agrave;n người sẽ mất việc l&agrave;m. Đơn cử, VNG hiện c&oacute; khoảng 1.800 nh&acirc;n sự, tất nhi&ecirc;n kh&ocirc;ng phải tất cả họ đều l&agrave;m về game nhưng nếu game online thực sự biết mất, sẽ c&oacute; kh&ocirc;ng &iacute;t người chẳng biết đi đ&acirc;u về đ&acirc;u.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Game biến mất rồi, tiệm net về đ&acirc;u...</strong></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Kh&ocirc;ng phải ai ra tiệm net cũng để chơi game online, nhưng kh&ocirc;ng thể phủ nhận game online l&agrave; chiếc cần c&acirc;u cơm lớn nhất của mọi tiệm net. Chưa c&oacute; con số thống k&ecirc; cụ thể lượng qu&aacute;n net được mở ở Việt Nam, nhưng chắc chắn đ&oacute; kh&ocirc;ng phải một con số nhỏ. Mở tiệm net đ&atilde; trở th&agrave;nh nghề mưu sinh của rất nhiều gia đ&igrave;nh. Một ng&agrave;y kh&ocirc;ng game online, nhiều chủ qu&aacute;n n&eacute;t sẽ chỉ biết phủi bụi cho d&agrave;n m&aacute;y của m&igrave;nh.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<span style="font-family:tahoma,geneva,sans-serif;"><img alt="Không game online, tiệm net về đâu?" border="1" height="284" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/4A/11/IMG_2455.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\n				<span style="font-family:tahoma,geneva,sans-serif;">Kh&ocirc;ng game online, tiệm net về đ&acirc;u?</span></td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Kh&ocirc;ng c&ograve;n nghề mưu sinh, ai biết được c&aacute;c chủ tiệm net sẽ l&agrave;m g&igrave; để kiếm sống. C&aacute;c Nh&agrave; cung cấp đường truyền internet cũng chịu ảnh hưởng kh&ocirc;ng nhỏ khi c&aacute;c tiệm net dần đ&oacute;ng cửa. V&agrave; lại c&oacute; th&ecirc;m rất nhiều người mất việc l&agrave;m.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Game biến mất rồi, huynh đệ về đ&acirc;u...</strong></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Game online vẽ ra một thế giới ảo. V&agrave; ở đ&oacute;, mọi người c&oacute; bạn b&egrave;, c&oacute; huynh đệ, c&oacute; gia đ&igrave;nh v&agrave; đ&ocirc;i khi c&oacute; cả kẻ th&ugrave;. Ch&iacute;nh t&iacute;nh cộng đồng đ&atilde; l&agrave;m n&ecirc;n sự hấp dẫn cho game online. V&agrave; nếu một mai, khi thế giới ảo ấy biến mất, nhiều game thủ sẽ thấy hụt hẫng, buồn ch&aacute;n khi thiếu vắng những người bạn nửa thực, nửa ảo n&agrave;y.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<span style="font-family:tahoma,geneva,sans-serif;"><img alt="Sẽ còn đâu những giờ anh em chung tay săn Boss?" border="1" height="252" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/4A/11/khong-game-3.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\n				<span style="font-family:tahoma,geneva,sans-serif;">Sẽ c&ograve;n đ&acirc;u những giờ anh em chung tay săn Boss?</span></td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Sẽ kh&ocirc;ng c&ograve;n nữa những l&uacute;c gọi nhau săn Boss, c&ocirc;ng th&agrave;nh; kh&ocirc;ng c&ograve;n nữa những lần đếm &quot;Perfect&quot; đợi kết h&ocirc;n hay những ph&uacute;t lẽo đẽo theo sau một nh&acirc;n vật xa lạ xin đồ, xin bạc. Quả thực, game online mang lại những tiếng cười, những ph&uacute;t gi&acirc;y giải tr&iacute;, thư gi&atilde;n kh&ocirc;ng k&eacute;m bất kỳ h&igrave;nh thức giải tr&iacute; n&agrave;o.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>V&agrave;...</strong></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Rất nhiều cuộc t&igrave;nh đ&atilde; nảy nở, đơm hoa kết tr&aacute;i từ thế giới game. Rất nhiều người đ&atilde; t&igrave;m được cơ hội kiếm tiền đơn giản bằng game online. Nhiều c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu đ&atilde; chỉ ra rằng, chơi game rất tốt cho cải thiện tr&iacute; nhớ v&agrave; thị gi&aacute;c của người gi&agrave;, game online c&oacute; t&aacute;c dụng t&iacute;ch cực đối với những bệnh nh&acirc;n trầm cảm. R&otilde; r&agrave;ng, game online đ&acirc;u phải chỉ to&agrave;n t&aacute;c hại.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<span style="font-family:tahoma,geneva,sans-serif;"><img alt="Không có game online, chắc gì họ đã đến được với nhau." border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/4A/11/khong-game-4.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\n				<span style="font-family:tahoma,geneva,sans-serif;">Kh&ocirc;ng c&oacute; game online, chắc g&igrave; họ đ&atilde; đến được với nhau.</span></td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Mọi thứ đều c&oacute; hai mặt của n&oacute;. Game online chắc chắn kh&ocirc;ng phải một thi&ecirc;n thần &aacute;o trắng, nhưng cũng kh&ocirc;ng phải một thứ chỉ mang lại nguy cơ. Chắc chắn, l&agrave;ng game sẽ buồn hơn nếu một ng&agrave;y kh&ocirc;ng c&oacute; game online.</span></p>\n'),
(107, 'Bất chấp nắng nóng, quán game tại Hà Nội vẫn đông khách', 'Bất chấp nắng nóng, quán game tại Hà Nội vẫn đông khách', 'Nhiều game thủ chọn các điểm Internet là nơi tránh nóng lý tưởng vào dịp này.', 'new_6.jpg', '14/05/2013', 7, 'gamelifes', '<p>\n	&nbsp;</p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="10">\n	<tbody>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2450.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Trong 2-3 ng&agrave;y gần đ&acirc;y, nhiệt độ thời tiết c&aacute;c khu vực miền Bắc, trong đ&oacute; c&oacute; thủ đ&ocirc; H&agrave; Nội, lu&ocirc;n ở mức cao từ 36-38 độ, c&oacute; nơi 39-40 độ. Thời tiết oi bức khiến cho đời sống sinh hoạt của mọi người d&acirc;n đều bị đảo lộn.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="249" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2451.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2453.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Tuy vậy, trong l&uacute;c đa phần người d&acirc;n đều t&igrave;m nơi tr&uacute; ẩn trong nh&agrave; với quạt v&agrave; điều h&ograve;a để tr&aacute;nh c&aacute;i nắng gay gắt th&igrave; kh&aacute; nhiều game thủ vẫn quyết t&acirc;m ra khỏi nh&agrave;, t&igrave;m đến c&aacute;c qu&aacute;n Internet để giải tr&iacute; với game online. C&oacute; thể thấy v&agrave;o tầm giữa trưa nhưng xe cộ của c&aacute;c &quot;thượng đế&quot; dựng trước cửa c&aacute;c qu&aacute;n Internet tr&ecirc;n đường L&ecirc; Thanh Nghị - một tụ điểm game online lớn tại H&agrave; Nội - vẫn kh&aacute; đ&ocirc;ng.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="286" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2454.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">B&ecirc;n trong qu&aacute;n, quạt v&agrave; điều h&ograve;a c&acirc;y chạy hết c&ocirc;ng suất để phục vụ c&aacute;c kh&aacute;ch h&agrave;ng. Song c&oacute; vẻ như do thời tiết qu&aacute; n&oacute;ng n&ecirc;n một số game thủ vẫn quyết định &quot;tr&uacute;t bỏ xi&ecirc;m y&quot; cho tho&aacute;ng m&aacute;t trong qu&aacute; tr&igrave;nh chiến game.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="284" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2455.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Mặc kệ trời n&oacute;ng, kh&ocirc;ng kh&iacute; chiến đấu kh&aacute; nhộn nhịp v&agrave; kh&ocirc;ng k&eacute;m phần căng thẳng của c&aacute;c xạ thủ tại một qu&aacute;n game.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2456.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Cơm hộp được xem l&agrave; m&oacute;n ăn ưa th&iacute;ch v&agrave; v&ocirc; c&ugrave;ng tiện lợi cho c&aacute;c game thủ giữa tiết trời khắc nghiệt n&agrave;y.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2458.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Theo chia sẻ từ một chủ tiệm Internet, v&agrave;i ng&agrave;y gần đ&acirc;y, mức ti&ecirc;u thụ c&aacute;c loại đồ ăn như b&aacute;nh mỳ, x&ocirc;i, cơm hộp của c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nhỉnh hơn ng&agrave;y thường một ch&uacute;t, chủ yếu v&agrave;o buổi trưa. Trong khi đ&oacute;, mặt h&agrave;ng nước giải kh&aacute;t như Coke, nước tăng lực được b&aacute;n nhiều hơn đợt m&aacute;t trước đ&oacute; khoảng 30-50%.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="276" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2447.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Một chủ qu&aacute;n Internet kh&aacute;c chia sẻ th&ecirc;m, lượng kh&aacute;ch đến qu&aacute;n những ng&agrave;y n&oacute;ng n&agrave;y kh&ocirc;ng hề giảm m&agrave; tr&aacute;i lại c&ograve;n tăng th&ecirc;m kh&aacute; nhiều ở một số khoảng thời gian nhất định, đặc biệt l&agrave; buổi trưa v&agrave; tầm chiều tối. Người n&agrave;y l&yacute; giải, kh&ocirc;ng &iacute;t kh&aacute;ch do nh&agrave; kh&ocirc;ng c&oacute; điều h&ograve;a n&ecirc;n tranh thủ ra qu&aacute;n, vừa được &quot;hưởng t&iacute; hơi m&aacute;t&quot; từ điều h&ograve;a của qu&aacute;n, vừa c&oacute; thể chơi game được thoải m&aacute;i hơn.</span></td>\n		</tr>\n		<tr>\n			<td style="text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;"><img border="1" height="275" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/E8/IMG_2445.jpg" width="480" /></span></td>\n		</tr>\n		<tr>\n			<td class="Normal" style="font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Dẫu vậy, thời tiết n&oacute;ng nực lại l&agrave; &quot;kẻ th&ugrave;&quot; của một số qu&aacute;n Internet kh&ocirc;ng được trang bị điều h&ograve;a, chẳng hạn như tại đ&acirc;y. Theo dự b&aacute;o thời tiết, trong 2-3 ng&agrave;y tới, đợt nắng n&oacute;ng đầu m&ugrave;a mới c&oacute; thể hạ nhiệt do chịu ảnh hưởng của một đợt kh&ocirc;ng kh&iacute; lạnh tr&agrave;n về từ ph&iacute;a Bắc.</span></td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	&nbsp;</p>\n'),
(108, 'Những ''huyền thoại'' Kim Dung trong game online Việt', 'Những ''huyền thoại'' Kim Dung trong game online Việt', 'Các cái tên Đoàn Dự, Đông Phương Bất Bại... đã ăn sâu vào tiềm thức game thủ.', 'new_7.jpg', '19/05/2013', 7, 'gamelifes', '<p>\n	&nbsp;</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Ra đời v&agrave; ph&aacute;t triển song h&agrave;nh c&ugrave;ng sự trưởng th&agrave;nh của l&agrave;ng game Việt, c&oacute; thể n&oacute;i l&agrave; tiểu thuyết Kim Dung l&agrave; đề t&agrave;i c&oacute; tầm ảnh hưởng v&agrave; độ phủ s&acirc;u rộng nhất trong số c&aacute;c đề t&agrave;i game online hiện nay. Qua thống k&ecirc; sơ bộ, c&oacute; tr&ecirc;n dưới 60% c&aacute;c tr&ograve; chơi trực tuyến ở VN lấy đề t&agrave;i Kim Dung, từ những &quot;c&acirc;y cổ thụ&quot;&nbsp;<em><a href="http://gamethu.vnexpress.net/search/?action=search&amp;cat=To%C3%A0n+b%E1%BB%99+c%C3%A1c+m%E1%BB%A5c&amp;sdate=&amp;edate=&amp;ds=1&amp;mc=False&amp;mt=False&amp;rp=10&amp;s=V%C3%B5%20L%C3%A2m%20Truy%E1%BB%81n%20K%E1%BB%B3" style="text-decoration: none; color: rgb(39, 42, 42);"><font color="#0080ff">V&otilde; L&acirc;m Truyền Kỳ</font></a>,&nbsp;<a href="http://gamethu.vnexpress.net/search/?action=search&amp;cat=To%C3%A0n+b%E1%BB%99+c%C3%A1c+m%E1%BB%A5c&amp;sdate=&amp;edate=&amp;ds=1&amp;mc=False&amp;mt=False&amp;rp=10&amp;s=Ki%E1%BA%BFm%20Th%E1%BA%BF" style="text-decoration: none; color: rgb(39, 42, 42);"><font color="#0080ff">Kiếm Thế</font></a>,&nbsp;<a href="http://gamethu.vnexpress.net/search/?action=search&amp;cat=To%C3%A0n+b%E1%BB%99+c%C3%A1c+m%E1%BB%A5c&amp;sdate=&amp;edate=&amp;ds=1&amp;mc=False&amp;mt=False&amp;rp=10&amp;s=Thi%C3%AAn%20Long%20B%C3%A1t%20B%E1%BB%99" style="text-decoration: none; color: rgb(39, 42, 42);"><font color="#0080ff">Thi&ecirc;n Long B&aacute;t Bộ</font></a></em>... cho đến h&agrave;ng loạt c&aacute;i t&ecirc;n mới gần đ&acirc;y như&nbsp;<em><a href="http://gamethu.vnexpress.net/search/?action=search&amp;cat=To%C3%A0n+b%E1%BB%99+c%C3%A1c+m%E1%BB%A5c&amp;sdate=&amp;edate=&amp;ds=1&amp;mc=False&amp;mt=False&amp;rp=10&amp;s=%C4%90%E1%BB%93%20Long%20%C4%90ao" style="text-decoration: none; color: rgb(39, 42, 42);"><font color="#0080ff">Đồ Long Đao</font></a>,&nbsp;<a href="http://gamethu.vnexpress.net/search/?action=search&amp;cat=To%C3%A0n+b%E1%BB%99+c%C3%A1c+m%E1%BB%A5c&amp;sdate=&amp;edate=&amp;ds=1&amp;mc=False&amp;mt=False&amp;rp=10&amp;s=Ngh%E1%BB%8Bch%20Thi%C3%AAn%20Quy%E1%BA%BFt" style="text-decoration: none; color: rgb(39, 42, 42);"><font color="#0080ff">Nghịch Thi&ecirc;n Quyết</font></a>,&nbsp;<a href="http://gamethu.vnexpress.net/search/?action=search&amp;cat=To%C3%A0n+b%E1%BB%99+c%C3%A1c+m%E1%BB%A5c&amp;sdate=&amp;edate=&amp;ds=1&amp;mc=False&amp;mt=False&amp;rp=10&amp;s=%C4%90%E1%BB%99c%20C%C3%B4%20C%E1%BA%A7u%20B%E1%BA%A1i" style="text-decoration: none; color: rgb(39, 42, 42);"><font color="#0080ff">Độc C&ocirc; Cầu Bại</font></a></em>...</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">D&ugrave; trải qua kh&ocirc;ng &iacute;t thăng trầm, thị phi, sau kh&ocirc;ng thể phủ nhận, c&aacute;c t&aacute;c phẩm của nh&agrave; văn Kim Dung c&ugrave;ng những nh&acirc;n vật của n&oacute; đ&atilde; ăn s&acirc;u v&agrave;o gốc rễ tiềm thức từng game thủ trực tiếp l&acirc;u năm ở VN. Bản th&acirc;n game online về đề t&agrave;i vẫn thường được cộng đồng đ&oacute;n nhận một c&aacute;ch nhiệt t&igrave;nh, được NPH Việt ưu ti&ecirc;n khi nhập về, d&ugrave; n&oacute; đ&atilde; qu&aacute; quen thuộc v&agrave; l&acirc;u đời.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">C&ugrave;ng&nbsp;<em>Game Thủ.net&nbsp;</em>điểm lại ch&acirc;n dung những nh&acirc;n vật Kim Dung đ&atilde; trở th&agrave;nh &quot;huyền thoại&quot; trong game online Việt.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Hư Tr&uacute;c</strong></span></p>\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" border="1" height="504" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/50/hu-truc.jpg" width="480" /></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Đ&acirc;y l&agrave; nh&acirc;n vật ch&iacute;nh trong&nbsp;<em>Thi&ecirc;n Long B&aacute;t Bộ&nbsp;</em>- t&aacute;c phẩm vĩ đại nhất của nh&agrave; văn Kim Dung với 50 hồi v&agrave; gần hai triệu chữ. Hư Tr&uacute;c l&agrave; đệ tử Thiếu L&acirc;m lu&ocirc;n một l&ograve;ng hướng Phật nhưng hết lần n&agrave;y đến lần kh&aacute;c lại gặp những cảnh trớ tr&ecirc;u đẩy m&igrave;nh v&agrave;o con đường... ăn mặn. Nhờ kỳ ngộ, Hư Tr&uacute;c học được v&otilde; c&ocirc;ng của V&ocirc; Nhai Tử v&agrave; Thi&ecirc;n Sơn Đồng L&atilde;o, trở th&agrave;nh trưởng m&ocirc;n ph&aacute;i Ti&ecirc;u Dao v&agrave; sau n&agrave;y l&agrave; ph&ograve; m&atilde; T&acirc;y Hạ.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Đo&agrave;n Dự</strong></span></p>\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" border="1" height="473" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/50/doan-du.jpg" width="480" /></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Vương tử nước Đại L&yacute; l&agrave; một thư sinh nho nh&atilde;, điển trai, kh&aacute; đ&agrave;o hoa v&agrave; rất gh&eacute;t học v&otilde; c&ocirc;ng. Trớ tr&ecirc;u thay, Đo&agrave;n Dự lại v&ocirc; t&igrave;nh hoặc bị &eacute;p học 3 m&ocirc;n v&otilde; c&ocirc;ng mạnh nhất trong&nbsp;<em>Thi&ecirc;n Long B&aacute;t Bộ</em>: Lục Mạch Thần Kiếm, Bắc Minh Thần C&ocirc;ng v&agrave; Lăng Ba Vi Bộ.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Ti&ecirc;u Phong</strong></span></p>\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" border="1" height="310" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/50/tieu-phong.jpg" width="480" /></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Ti&ecirc;u Phong (t&ecirc;n thật: Kiều Phong) - bang chủ đời thứ 15 của C&aacute;i Bang - l&agrave; một anh h&ugrave;ng hiệp nghĩa, kh&iacute; bạt v&acirc;n thi&ecirc;n. Tuy nhi&ecirc;n, cuộc đời của Ti&ecirc;u Phong gặp nhiều s&oacute;ng gi&oacute; bởi th&acirc;n phận Khiết Đan. Đ&acirc;y cũng l&agrave; nh&acirc;n vật được nhắc đến nhiều nhất trong&nbsp;<em>Thi&ecirc;n Long B&aacute;t Bộ</em>, đồng thời l&agrave; một trong số c&aacute;c nh&acirc;n vật được y&ecirc;u th&iacute;ch nhất của Kim Dung. Với tuyệt chi&ecirc;u H&agrave;ng Long Thập B&aacute;t Chưởng, Ti&ecirc;u Phong gần như kh&ocirc;ng c&oacute; đối thủ.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>L&atilde;o ngoan đồng Chu B&aacute; Th&ocirc;ng</strong></span></p>\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" border="1" height="270" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/50/chu-ba.jpg" width="480" /></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Chu B&aacute; Th&ocirc;ng l&agrave; một nh&acirc;n vật nổi tiếng trong<em>&nbsp;Anh H&ugrave;ng Xạ Đi&ecirc;u</em>&nbsp;v&agrave;&nbsp;<em>Thần Đi&ecirc;u Hiệp Lữ&nbsp;</em>với t&iacute;nh nết ng&acirc;y thơ như trẻ con nhưng lại cuồng v&otilde; thuật. Trong thời gian rảnh rỗi ở Đ&agrave;o Hoa Đảo, &ocirc;ng đ&atilde; tự s&aacute;ng chế ra hai v&otilde; c&ocirc;ng độc m&ocirc;n: Song Thủ Hỗ B&aacute;c v&agrave; Kh&ocirc;ng Minh Quyền.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><strong>Đ&ocirc;ng Phương Bất Bại</strong></span></p>\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" border="1" height="260" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/50/dong-phuong.jpg" width="480" /></span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Đ&ocirc;ng Phương Bất Bại l&agrave; một trong những nh&acirc;n vật mạnh nhất của Kim Dung. Xứng đ&aacute;ng với c&aacute;i t&ecirc;n Bất Bại, thực sự chưa ai đủ khả năng đ&aacute;nh bại nh&acirc;n vật n&agrave;y. Với vũ kh&iacute; sở trường l&agrave; c&acirc;y quạt giấy v&agrave; kim th&ecirc;u, Đ&ocirc;ng Phương Bất Bại l&agrave; nỗi &aacute;m ảnh của mọi nh&acirc;n sĩ v&otilde; l&acirc;m trong&nbsp;<em>Tiếu Ngạo Giang Hồ</em>.</span></p>\n'),
(109, 'Một game thủ eSports Hàn Quốc vừa qua đời ở tuổi 25', 'Một game thủ eSports Hàn Quốc vừa qua đời ở tuổi 25', 'Mắc chứng Loạn dưỡng cơ, Seung Hyun Park (hay còn được gọi là Go)Space) từng được các bác sĩ chẩn đoán là sẽ không sống nổi qua tuổi 20.', 'new_8.jpg', '20/05/2013', 7, 'gamelifes', '<p>\n	&nbsp;</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Cộng đồng game thủ eSports H&agrave;n Quốc vừa đ&oacute;n nhận một tin buồn khi game thủ&nbsp;<em>Warcraft III</em>&nbsp;chuy&ecirc;n nghiệp, Seung Hyun Park (nick name l&agrave; Go)Space) vừa qua đời h&ocirc;m thứ hai (6/5) vừa qua. Anh mất ở tuổi 25.<br />\n	<br />\n	Theo&nbsp;<em>MyDaily,&nbsp;</em>Park bị mắc chứng loạn dưỡng cơ (Muscular Dystrophy - khiến người bệnh gặp phải t&igrave;nh trạng cơ teo dần). Được chẩn đo&aacute;n bệnh khi 11 tuổi, c&aacute;c b&aacute;c sĩ tin rằng anh kh&oacute; c&oacute; thể ăn mừng được sinh nhật tuổi 20 của m&igrave;nh. Tuy nhi&ecirc;n, thực tế đ&atilde; tạo ra một điều kỳ diệu khi kh&ocirc;ng những sống qua được mốc tuổi giới hạn th&ecirc;m 5 năm, Park c&ograve;n trở th&agrave;nh một game thủ chuy&ecirc;n nghiệp trong bộ m&ocirc;n&nbsp;<em>Warcraft III</em>&nbsp;to&agrave;n thế giới.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<p align="center">\n					<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" alt="Park trả lời câu hỏi của một người dẫn chương trình." border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/4F/esports_(2).jpg" width="480" /></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">Park trả lời c&acirc;u hỏi của người dẫn chương tr&igrave;nh tại một sự kiện game.</span></td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">&quot;B&acirc;y giờ t&ocirc;i kh&ocirc;ng thể l&agrave;m được bất cứ việc g&igrave;&quot;, Park n&oacute;i trong một cuộc phỏng vấn năm 2008 (tr&ecirc;n trang game<em>TeamLiquid</em>). &quot;T&ocirc;i kh&ocirc;ng thể đọc s&aacute;ch một m&igrave;nh v&agrave; cũng kh&ocirc;ng thể ra ngo&agrave;i. &Iacute;t nhất th&igrave; t&ocirc;i c&oacute; thể di chuyển được ng&oacute;n tay, v&igrave; vậy t&ocirc;i c&oacute; thể chơi game. Nhưng sức khỏe của t&ocirc;i đang trở n&ecirc;n tệ hơn theo thời gian, t&ocirc;i kh&ocirc;ng chắc m&igrave;nh c&oacute; thể l&agrave;m vậy cho đến bao giờ...&quot;<br />\n	<br />\n	Cũng trong cuộc phỏng vấn n&agrave;y, mẹ của Park chia sẻ rằng trong khi c&aacute;c bậc cha mẹ kh&aacute;c sợ việc con c&aacute;i của họ trở n&ecirc;n nghiện game th&igrave; c&ocirc; lại lu&ocirc;n lo lắng về những ng&oacute;n tay của con trai m&igrave;nh sẽ trở n&ecirc;n t&ecirc; liệt v&agrave; kh&ocirc;ng thể chơi được nữa.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<p align="center">\n					<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" alt="Hình ảnh Seung Hyun Park khi thi đấu." border="1" height="370" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/4F/esports.jpg" width="480" /></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">H&igrave;nh ảnh Seung Hyun Park khi thi đấu.</span></td>\n		</tr>\n	</tbody>\n</table>\n<p align="left" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">Trong v&agrave;i th&aacute;ng qua, nhiều chủ đề thảo luận về game thủ đặc biệt n&agrave;y đ&atilde; xuất hiện tr&ecirc;n c&aacute;c diễn đ&agrave;n game nổi tiếng, phần lớn đều b&agrave;y tỏ sự lo ngại về sức khỏe của Park.</span></p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<span style="font-family:tahoma,geneva,sans-serif;">&quot;C&oacute; một game thủ&nbsp;<em>WarCraft III</em>&nbsp;chơi Undead tuyệt đỉnh được gọi l&agrave; Go)Space, anh được chẩn đo&aacute;n mắc chứng rối loạn cơ bắp hiếm khi 11 tuổi. Anh ấy kh&ocirc;ng thể l&agrave;m g&igrave; ngo&agrave;i việc di chuyển những ng&oacute;n tay. Nhưng t&ocirc;i đ&atilde; xem anh ta chơi như thế n&agrave;o v&agrave; hạ gục những t&ecirc;n tuổi lớn của l&agrave;ng&nbsp;<em>WarCraft III</em>&nbsp;thế giới. N&oacute; giống như một ph&eacute;p lạ&quot;, một th&agrave;nh vi&ecirc;n đ&atilde; hỏi về Park.<br />\n	<br />\n	&quot;Ước mơ của t&ocirc;i kh&ocirc;ng phải l&agrave; trở th&agrave;nh một game thủ chuy&ecirc;n nghiệp v&agrave; cũng kh&ocirc;ng phải l&agrave; sức khỏe. T&ocirc;i kh&ocirc;ng tin v&agrave;o ph&eacute;p lạ. Tuy nhi&ecirc;n, t&ocirc;i hy vọng m&igrave;nh sẽ l&agrave; game thủ tốt nhất trong&nbsp;<em>Warcraft III</em>, đ&oacute; l&agrave; điều duy nhất t&ocirc;i c&oacute; thể l&agrave;m b&acirc;y giờ để cho gia đ&igrave;nh m&igrave;nh c&oacute; thể tự h&agrave;o&quot;, Park từng chia sẻ.</span></p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<p align="center">\n					<span style="font-family:tahoma,geneva,sans-serif;"><img align="center" alt="&quot;Park không chỉ làm cho gia đình mình tự hào. Anh đã khiến tất cả các game thủ trên thế giới có thể tự hào. Go)Space, hãy yên nghỉ!&quot;" border="1" height="320" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/4F/esports_(1).jpg" width="480" /></span></p>\n			</td>\n		</tr>\n		<tr>\n			<td class="Image" style="font-size: 11px; color: rgb(82, 77, 79); text-align: center;">\n				<span style="font-family:tahoma,geneva,sans-serif;">&quot;Park kh&ocirc;ng chỉ l&agrave;m cho gia đ&igrave;nh m&igrave;nh tự h&agrave;o. Anh đ&atilde; khiến tất cả c&aacute;c game thủ tr&ecirc;n thế giới c&oacute; thể tự h&agrave;o. Go)Space, h&atilde;y y&ecirc;n nghỉ!&quot;</span></td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	&nbsp;</p>\n'),
(110, 'Tin moi them 666', 'Trich dan moi them 666', 'Noi dung tom tat moi them 666', 'b_22.jpg', '06/06/2013', 1, 'news', '<p>\r\n	zzzzzzzzzzzzzzzzzzzzzzzzzz zzzzzzzzzzzzz</p>\r\n'),
(111, 'Tieu de moi them 101010', 'Trich dan 101010', 'Noi dung ngan 101010', 'b_18.jpg', '06/06/2013', 1, 'news', '<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	V&agrave;i tuần trở lại đ&acirc;y, sự ch&uacute; &yacute; của cộng đồng đ&atilde; v&agrave; đang đổ dồn v&agrave;o sự xuất hiện của teaser b&iacute; ẩn&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>. Nguy&ecirc;n do l&agrave; c&aacute;i t&ecirc;n của n&oacute; c&oacute; li&ecirc;n quan mật thiết đến&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm X7&nbsp;</em>- MMORPG 3D đang nổi đ&igrave;nh nổi đ&aacute;m ở Trung Quốc của Changyou. Kh&ocirc;ng &iacute;t người b&agrave;y tỏ hy vọng, dự &aacute;n b&iacute; ẩn đằng sau trang giới thiệu n&agrave;y sẽ l&agrave;&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm X7&nbsp;</em>để họ c&oacute; th&ecirc;m một lựa chọn MMO hấp dẫn nữa cho kỳ nghỉ h&egrave; sắp tới.</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	Trong một b&agrave;i ph&acirc;n t&iacute;ch gần đ&acirc;y nhất,&nbsp;<em>Game Thủ.net</em>&nbsp;từng đưa ra dự đo&aacute;n về việc dự &aacute;n b&iacute; ẩn n&agrave;y chỉ đơn thuần l&agrave; một gMO chứ kh&ocirc;ng phải MMORPG của Changyou. Rốt cuộc, dự đo&aacute;n đ&oacute; đ&atilde; trở th&agrave;nh sự thực khi h&ocirc;m nay 13/5, quản trị fanpage&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm&nbsp;</em>đ&atilde; đăng đ&agrave;n tuy&ecirc;n bố đ&acirc;y ch&iacute;nh l&agrave; một gMO từ VTC Mobile.</p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<img alt="Lời công bố của admin trên fanpage Hiên Viên Kiếm." border="1" height="281" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/9C/hien-vien.jpg" width="480" /></td>\n		</tr>\n		<tr>\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\n				Lời c&ocirc;ng bố của admin tr&ecirc;n fanpage Hi&ecirc;n Vi&ecirc;n Kiếm.</td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	Cụ thể theo lời admin fanpage, gMO&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>&nbsp;xuất ph&aacute;t từ nội dung, bối cảnh nhiều lớp của game. &quot;Giữa năm 2012 khi VTC Mobile Game ch&iacute;nh thức ph&aacute;t h&agrave;nh gMO&nbsp;<em>Ho&agrave;ng Đế,</em>&nbsp;vốn c&oacute; t&ecirc;n gốc l&agrave;&nbsp;<em>Hi&ecirc;n Vi&ecirc;n</em>, kể về Ho&agrave;ng đế Hi&ecirc;n Vi&ecirc;n c&ugrave;ng thanh kiếm thần dẹp y&ecirc;n loạn lạc.&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm&nbsp;</em>l&agrave; phần tiếp nối của&nbsp;<em>Ho&agrave;ng Đế&nbsp;</em>với bối cảnh hậu thế truy t&igrave;m thanh kiếm của Ho&agrave;ng đế Hi&ecirc;n Vi&ecirc;n đ&atilde; bị thất lạc. Đ&acirc;y cũng ch&iacute;nh l&agrave; một trong những chiến lược của VTC Mobile Game khi ph&aacute;t triển c&aacute;c game của m&igrave;nh theo một hệ thống, c&oacute; mối li&ecirc;n quan chặt chẽ&quot;, vị quản trị cho biết.</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	Cũng theo th&ocirc;ng tin tr&ecirc;n fanpage,&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>&nbsp;l&agrave; gMO đề cao t&iacute;nh bang hội, khả năng kết hợp nh&oacute;m. Hiện tại game đang v&agrave;o chặng cuối của Alpha Test (test nội bộ) trước khi mở cửa Closed Beta.</p>\n<table align="center" border="0" cellpadding="3" cellspacing="0" style="color: rgb(0, 0, 0); font-family: Arial; font-size: medium; background-color: rgb(240, 237, 237);" width="1">\n	<tbody>\n		<tr>\n			<td>\n				<img alt="Hiên Viên Kiếm không phải là MMORPG như một số người dự đoán." border="1" height="282" src="http://gamethu.vnexpress.net/Files/Subject/3B/9B/49/9C/hien-vien..jpg" width="480" /></td>\n		</tr>\n		<tr>\n			<td align="center" class="Image" style="font-size: 11px; color: rgb(82, 77, 79);">\n				<em>Hi&ecirc;n Vi&ecirc;n Kiếm</em>&nbsp;kh&ocirc;ng phải l&agrave; MMORPG như một số người dự đo&aacute;n.</td>\n		</tr>\n	</tbody>\n</table>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	Phản ứng trước th&ocirc;ng tin tr&ecirc;n fanpage&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm,&nbsp;</em>một số game thủ b&agrave;y tỏ sự thất vọng r&otilde; rệt do trước đ&oacute; họ từng đinh ninh đ&acirc;y ch&iacute;nh l&agrave;&nbsp;<em>Hi&ecirc;n Vi&ecirc;n Kiếm X7 -&nbsp;</em>MMORPG 3D từ Changyou. &quot;Ặc, tưởng game client 3D ho&agrave;nh tr&aacute;ng lắm, ai ngờ l&agrave; game mobile&quot;, một game thủ nhăn nh&oacute;.</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	Trong khi đ&oacute;, một số người kh&aacute;c lại tỏ ra kh&aacute; b&igrave;nh thản khi dường như đ&atilde; đo&aacute;n trước ch&acirc;n tướng sự việc. Những người n&agrave;y li&ecirc;n tục đặt ra c&aacute;c c&acirc;u hỏi cho admin về tr&ograve; chơi như game c&oacute; h&igrave;nh ảnh ra sao, game sẽ hỗ trợ d&ograve;ng m&aacute;y n&agrave;o...</p>\n<p class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<em>Game Thủ.net</em>&nbsp;sẽ tiếp tục cập nhật th&ocirc;ng tin chi tiết về dự &aacute;n n&agrave;y.</p>\n<p align="right" class="Normal" style="font-family: Arial; font-size: 12px; margin: 5px 0px 15px; color: rgb(39, 42, 42); background-color: rgb(240, 237, 237);">\n	<strong>Ho&agrave;ng Qu&acirc;n</strong></p>\n');

