INSERT INTO `sliders` (`slider_id`, `slider_title`, `slider_content`, `slider_image`, `slider_active`) VALUES
(1, 'Battle Field 3 : Chuyến Tuyến Rực Lữa', 'Một lợi thế duy nhất và không thể chối cãi của Battlefield 3 so với các tựa Game khác, đặc biệt với kẻ thù không đội trời chung Call of Duty: Modern Warfare 3, là một nền đồ họa vô cùng tân tiến, khả năng điều khiển các khí tài quân sự cùng phong cách đấu súng tả thật pha trộn một chút tính chiến thuật bên trong.', 'battlefield.jpg', b'1'),
(2, 'Dead Space 3 Awakened – Bản mở rộng kinh hoàng', 'Dù đã hơn một tháng trôi qua kể từ ngày Dead Space 3 được phát hành chính thức nhưng cho tới thời điểm này, sự thắc mắc và tò mò của các fan hâm mộ về tính mệnh người anh hùng Isaac Clarke cùng người bạn đồng hành John Carver vẫn còn đang rất nóng hổi trên khắp các diễn đàn.', 'deadspace3.jpg', b'1'),
(3, 'Dishonored : The Knife Of Dunwall', 'Là một trong những game thành công nhất được phát hành trong năm 2012. Nó gây được tiếng vang lớn, được game thủ toàn cầu đón nhận nồng nhiệt đồng nghĩa với doanh số khả quan. Các số liệu tài chính cụ thể của Dishonored không được công bố song ảnh hưởng của trò chơi này với nền công nghiệp game là điều không khó để nhận ra.', 'dishonored3.jpg', b'1'),
(4, 'Tomb Raider 3 : Bản Năng Sinh Tồn', 'Phiên bản mới nhất của Series Game nổi tiếng Tomb Raider đã ra mắt vào ngày 5/3 vừa qua và được đánh giá rất cao, game đã đạt 1 triệu lượt người chơi chỉ sau 2 ngày', 'tombraider3.jpg', b'1'),
(50, '0', '0', 'a_21.jpg', b'0'),
